pragma solidity ^0.8.9;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {ExtCallACL} from "../libraries/acl/ExtCallACL.sol";
import {ExtCallACLProxy} from "../libraries/acl/ExtCallACLProxy.sol";
import {DataTypes} from "../libraries/types/DataTypes.sol";
import {ReserveLogic} from "../libraries/logic/ReserveLogic.sol";
import {Errors} from "../libraries/helpers/Errors.sol";
import {AddressResolver} from "../component/infra/AddressResolver.sol";
import {VaultManager} from "../component/vault/VaultManager.sol";
import {VaultManagerProxy} from "../component/vault/VaultManagerProxy.sol";
import {LiquidityManager} from "../component/liquidity/LiquidityManager.sol";
import {LoanManager} from "../component/loan/LoanManager.sol";
import {IACLManager} from "../interfaces/IACLManager.sol";
import {MessageBus} from "./infra/MessageBus.sol";

import {Diamond, IDiamondCut, LibDiamond} from "../diamond/Diamond.sol";

contract Voyager is Diamond, MessageBus {
    modifier onlyWhitelisted(bytes32 func) {
        require(
            ExtCallACL(getExtCallACLProxyAddress()).isWhitelistedAddress(
                msg.sender
            ),
            "Voyager: not whitelisted address"
        );
        require(
            ExtCallACL(getExtCallACLProxyAddress()).isWhitelistedFunction(func),
            "Voyager: not whitelisted functions"
        );
        _;
    }

    modifier onlyProtocolManager() {
        _requireCallerAdmin();
        _;
    }

    modifier onlyOwner() {
        require(msg.sender == LibDiamond.contractOwner());
        _;
    }

    event CallResult(bool, bytes);

    constructor(
        IDiamondCut.FacetCut[] memory _diamondCut,
        DiamondArgs memory _args
    ) Diamond(_diamondCut, _args) {}

    /************************************** HouseKeeping Interfaces **************************************/
    /**
     * @dev Update addressResolver contract address
     * @param _addressResolver address of the resolver contract
     **/
    function setAddressResolverAddress(address _addressResolver)
        external
        onlyOwner
    {
        addressResolver = AddressResolver(_addressResolver);
    }

    function whitelistAddress(address[] calldata _address)
        external
        onlyProtocolManager
    {
        ExtCallACL extCallACL = ExtCallACL(getExtCallACLProxyAddress());
        extCallACL.whitelistAddress(_address);
    }

    function whitelistFunction(bytes32[] calldata _function)
        external
        onlyProtocolManager
    {
        ExtCallACL extCallACL = ExtCallACL(getExtCallACLProxyAddress());
        extCallACL.whitelistFunction(_function);
    }

    /************************************** Liquidity Manager Interfaces **************************************/

    /**
     * @dev Deposits an `amount` of underlying asset into the reserve, receiving in return overlying tokens: Either
     * Junior Deposit Token or Senior Deposit token
     * @param _asset The address of the underlying asset to deposit
     * @param _tranche The tranche of the liquidity pool the user wants to deposit to
     * @param _amount The amount to be deposited
     **/
    function deposit(
        address _asset,
        ReserveLogic.Tranche _tranche,
        uint256 _amount
    ) external {
        LiquidityManager(getLiquidityManagerProxyAddress()).deposit(
            _asset,
            _tranche,
            _amount,
            msg.sender
        );
    }

    function withdraw(
        address _asset,
        ReserveLogic.Tranche _tranche,
        uint256 _amount
    ) external {
        LiquidityManager(getLiquidityManagerProxyAddress()).withdraw(
            _asset,
            _tranche,
            _amount,
            payable(msg.sender)
        );
    }

    /**
     * @dev Returns the reserve flags
     * @param _asset The address of asset
     * @return The state flags representing active, frozen, borrowing enabled
     **/
    function getReserveFlags(address _asset)
        external
        view
        returns (
            bool,
            bool,
            bool
        )
    {
        return
            LiquidityManager(getLiquidityManagerProxyAddress()).getFlags(
                _asset
            );
    }

    /**
     * @dev Returns the configuration of the reserve
     * @param _asset The address of the underlying asset of the reserve
     * @return The state of the reserve
     **/
    function getConfiguration(address _asset)
        external
        view
        returns (DataTypes.ReserveConfigurationMap memory)
    {
        require(Address.isContract(_asset), Errors.LM_NOT_CONTRACT);
        return
            LiquidityManager(getLiquidityManagerProxyAddress())
                .getConfiguration(_asset);
    }

    /**
     * @dev Get current liquidity rate for a specific reserve for it junior tranche or senior tranche
     * @param _asset The address of the underlying asset of the reserve
     * @param _tranche Either junior tranche or senior tranche
     **/
    function liquidityRate(address _asset, ReserveLogic.Tranche _tranche)
        external
        view
        returns (uint256)
    {
        return
            LiquidityManager(getLiquidityManagerProxyAddress())
                .getLiquidityRate(_asset, _tranche);
    }

    /************************************** Loan Manager Interfaces **************************************/

    // todo remove _grossAssetValue
    function borrow(
        address _asset,
        uint256 _amount,
        address payable _vault,
        uint256 _grossAssetValue
    ) external onlyWhitelisted("borrow") {
        LoanManager(addressResolver.getLoanManagerProxy()).borrow(
            msg.sender,
            _asset,
            _amount,
            _vault,
            _grossAssetValue
        );
    }

    function repay(
        address _asset,
        uint256 _drawDownIdx,
        address payable _vault
    ) external {
        LoanManager(addressResolver.getLoanManagerProxy()).repay(
            msg.sender,
            _asset,
            _drawDownIdx,
            _vault
        );
    }

    /************************************** Vault Manager Interfaces **************************************/

    /**
     * @dev Create an empty Vault for msg.sender, in addition to this, a vault also deploy
     * a SecurityDepositEscrow contract which the fund will be held in
     × @return address of Vault
     **/
    function createVault(
        address _to,
        address _reserve,
        bytes32 _salt
    ) external onlyWhitelisted("createVault") returns (address) {
        address vaultManagerProxy = getVaultManagerProxyAddress();
        VaultManager vaultManager = VaultManager(vaultManagerProxy);
        return vaultManager.createVault(_to, _reserve, _salt);
    }

    function initVault(address _vault, address _reserve) external {
        address vaultManagerProxy = getVaultManagerProxyAddress();
        VaultManager vaultManager = VaultManager(vaultManagerProxy);
        vaultManager.initVault(_vault, _reserve);
    }

    /**
     * @dev Deposit specific amount of security deposit to user owned Vault
     * @param _vaultUser the user address that will be sponsored
     * @param _reserve address of reserve
     * @param _amount deposit amount
     **/
    function depositMargin(
        address _vaultUser,
        address _reserve,
        uint256 _amount
    ) external onlyWhitelisted("depositMargin") {
        VaultManager(getVaultManagerProxyAddress()).depositMargin(
            msg.sender,
            _vaultUser,
            _reserve,
            _amount
        );
    }

    /**
     * @dev Redeem specific amount of security deposit to user owned Vault
     * @param _vaultUser the user address that has be sponsored
     * @param _reserve address of reserve
     * @param _amount deposit amount
     **/
    function redeemMargin(
        address _vaultUser,
        address _reserve,
        uint256 _amount
    ) external onlyWhitelisted("redeemMargin") {
        VaultManager(getVaultManagerProxyAddress()).redeemMargin(
            payable(msg.sender),
            _vaultUser,
            _reserve,
            _amount
        );
    }

    // todo placeholder functions, more detail should be impl in the future
    function slash(
        address _vaultUser,
        address _reserve,
        address payable _to,
        uint256 _amount
    ) external {
        VaultManager(getVaultManagerProxyAddress()).slash(
            _vaultUser,
            _reserve,
            _to,
            _amount
        );
    }

    /**
     * @dev Get maximum reserve amount the use can borrow
     * @param _user user address
     * @param _reserve reserve contract address
     **/
    function getCreditLimit(address _user, address _reserve)
        external
        view
        returns (uint256)
    {
        return
            VaultManagerProxy(getVaultManagerProxyAddress()).getCreditLimit(
                _user,
                _reserve
            );
    }

    /**
     * @dev Get available credit
     * @param _user user address
     * @param _reserve address of reserve
     **/
    function getAvailableCredit(address _user, address _reserve)
        external
        view
        returns (uint256)
    {
        return
            VaultManagerProxy(getVaultManagerProxyAddress()).getAvailableCredit(
                _user,
                _reserve
            );
    }

    // deprecated
    function eligibleAmount(
        address _vaultUser,
        address _reserve,
        address _sponsor
    ) external view returns (uint256) {
        return
            VaultManager(getVaultManagerProxyAddress()).getWithdrawableDeposit(
                _vaultUser,
                _reserve,
                _sponsor
            );
    }

    /************************************** View Interfaces **************************************/

    /**
     * @dev Get ExtCallACLProxy contract address
     **/
    function getExtCallACLProxyAddress() public view returns (address payable) {
        address extCallACLProxyAddress = AddressResolver(addressResolver)
            .getExtCallProxy();
        return payable(extCallACLProxyAddress);
    }

    /************************************** Internal Interfaces **************************************/

    function _requireCallerAdmin() internal view {
        IACLManager aclManager = IACLManager(addressResolver.getAclManager());
        require(aclManager.isProtocolManager(tx.origin), "Not vault admin");
    }
}
","keccak256":"0x43d652d9159dc586ba81520f9e531af03a696e8fefdcde811179396e0e8b0834","license":"GPL-3.0"},"contracts/component/infra/AddressResolver.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {IAddressResolver} from "../../interfaces/IAddressResolver.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

contract AddressResolver is IAddressResolver, Ownable {
    bytes32 public constant voyageName = "voyager";
    bytes32 public constant aclManagerName = "aclManager";
    bytes32 public constant liquidityManagerProxyName = "liquidityManagerProxy";
    bytes32 public constant liquidityManagerName = "liquidityManager";
    bytes32 public constant liquidityManagerStorageName =
        "liquidityManagerStorage";
    bytes32 public constant liquidityDepositEscrowName =
        "liquidityDepositEscrow";
    bytes32 public constant loanManagerProxyName = "loanManagerProxy";
    bytes32 public constant loanManagerName = "loanManager";
    bytes32 public constant vaultManagerProxyName = "vaultManagerProxy";
    bytes32 public constant vaultManagerName = "vaultManager";
    bytes32 public constant vaultStorageName = "vaultStorage";
    bytes32 public constant juniorDepositTokenName = "juniorDepositToken";
    bytes32 public constant seniorDepositTokenName = "seniorDepositToken";
    bytes32 public constant securityDepositTokenName = "securityDepositToken";
    bytes32 public constant extCallACLProxyName = "extCallACLProxy";
    bytes32 public constant priceOracleName = "priceOracle";

    mapping(bytes32 => address) public repository;

    function importAddresses(
        bytes32[] calldata names,
        address[] calldata destinations
    ) external onlyOwner {
        require(
            names.length == destinations.length,
            "Input lengths must match"
        );

        for (uint256 i = 0; i < names.length; i++) {
            bytes32 name = names[i];
            address destination = destinations[i];
            repository[name] = destination;
            emit AddressImported(name, destination);
        }
    }

    function getVoyage() external view returns (address) {
        return repository[voyageName];
    }

    function getLiquidityManagerProxy() external view returns (address) {
        return repository[liquidityManagerProxyName];
    }

    function getLiquidityManagerStorage() external view returns (address) {
        return repository[liquidityManagerStorageName];
    }

    function getLoanManagerProxy() external view returns (address) {
        return repository[loanManagerProxyName];
    }

    function getLoanManager() external view returns (address) {
        return repository[loanManagerName];
    }

    function getVaultManagerProxy() external view returns (address) {
        return repository[vaultManagerProxyName];
    }

    function getVaultManager() external view returns (address) {
        return repository[vaultManagerName];
    }

    function getVaultStorage() external view returns (address) {
        return repository[vaultStorageName];
    }

    function getJuniorDepositToken() external view returns (address) {
        return repository[juniorDepositTokenName];
    }

    function getSeniorDepositToken() external view returns (address) {
        return repository[seniorDepositTokenName];
    }

    function getExtCallProxy() external view returns (address) {
        return repository[extCallACLProxyName];
    }

    function getAclManager() external view returns (address) {
        return repository[aclManagerName];
    }

    function getPriceOracle() external view returns (address) {
        return repository[priceOracleName];
    }

    function getAddress(bytes32 name) external view returns (address) {
        return repository[name];
    }

    function requireAndGetAddress(bytes32 name, string calldata reason)
        external
        view
        returns (address)
    {
        address _foundAddress = repository[name];
        require(_foundAddress != address(0), reason);
        return _foundAddress;
    }
}
","keccak256":"0x13cd3ca7121dc7d9a8169a56c4ca3572fee97a9ec8b6145d17d702656818dd66","license":"GPL-3.0"},"contracts/component/infra/MessageBus.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {AddressResolver} from "./AddressResolver.sol";
import {IMessageBus} from "../../interfaces/IMessageBus.sol";
import {IVaultManager} from "../../interfaces/IVaultManager.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {DataTypes} from "../../libraries/types/DataTypes.sol";
import {Errors} from "../../libraries/helpers/Errors.sol";
import {LiquidityManager} from "../liquidity/LiquidityManager.sol";
import {VaultManager} from "../vault/VaultManager.sol";
import {VaultManagerProxy} from "../vault/VaultManagerProxy.sol";

/**
 * todo it might be a bad name here, it actually performs as the centralise place
 * for querying each other among the internal components
 **/
contract MessageBus is IMessageBus {
    AddressResolver public addressResolver;

    /**
     * @dev Get addressResolver contract address
     * @return address of the resolver contract
     **/
    function getAddressResolverAddress() external view returns (address) {
        return address(addressResolver);
    }

    /************************************** LiquidityManager Functions **************************************/

    /**
     * @dev Get LiquidityManagerProxy contract address
     **/
    function getLiquidityManagerProxyAddress()
        public
        view
        returns (address payable)
    {
        address liquidityManagerProxyAddress = addressResolver
            .getLiquidityManagerProxy();
        return payable(liquidityManagerProxyAddress);
    }

    /**
     * @dev Returns the state and configuration of the reserve
     * @param _asset The address of the underlying asset of the reserve
     * @return The state of the reserve
     **/
    function getReserveData(address _asset)
        external
        view
        returns (DataTypes.ReserveData memory)
    {
        require(Address.isContract(_asset), Errors.LM_NOT_CONTRACT);
        return
            LiquidityManager(getLiquidityManagerProxyAddress()).getReserveData(
                _asset
            );
    }

    /************************************** Vault Functions **************************************/

    function getVaultConfig(address _reserve)
        external
        view
        returns (DataTypes.VaultConfig memory)
    {
        require(Address.isContract(_reserve), Errors.LM_NOT_CONTRACT);
        return
            VaultManagerProxy(getVaultManagerProxyAddress()).getVaultConfig(
                _reserve
            );
    }

    /**
     * @dev Get vault address
     * @param _user The owner of the vault
     **/
    function getVault(address _user) external view returns (address) {
        return VaultManagerProxy(getVaultManagerProxyAddress()).getVault(_user);
    }

    function getSecurityDeposit(address _user, address _reserve)
        external
        view
        returns (uint256)
    {
        uint256 amt = VaultManagerProxy(getVaultManagerProxyAddress())
            .getSecurityDeposit(_user, _reserve);
        return amt;
    }

    /**
     * @dev Get VaultManagerProxy contract address
     * @return address of the VaultManager
     **/
    function getVaultManagerProxyAddress()
        public
        view
        returns (address payable)
    {
        address vaultManagerProxyAddress = addressResolver
            .getVaultManagerProxy();
        return payable(vaultManagerProxyAddress);
    }
}
