pragma solidity ^0.8.9;

import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {SafeMath} from "@openzeppelin/contracts/utils/math/SafeMath.sol";
import {Voyager} from "../Voyager.sol";
import {Errors} from "../../libraries/helpers/Errors.sol";
import {LiquidityManagerStorage} from "../shared/storage/LiquidityManagerStorage.sol";
import {AddressResolver} from "../infra/AddressResolver.sol";
import {Proxyable} from "../../libraries/proxy/Proxyable.sol";
import {ReserveLogic} from "../../libraries/logic/ReserveLogic.sol";
import {DataTypes} from "../../libraries/types/DataTypes.sol";
import {IReserveManager} from "../../interfaces/IReserveManager.sol";
import {IVoyagerComponent} from "../../interfaces/IVoyagerComponent.sol";
import {IACLManager} from "../../interfaces/IACLManager.sol";

abstract contract ReserveManager is Proxyable, IReserveManager {
    constructor(address payable _proxy, address payable _voyager)
        Proxyable(_proxy)
    {
        voyager = Voyager(_voyager);
    }

    modifier onlyAdmin() {
        _requireCallerAdmin();
        _;
    }

    /************************************** HouseKeeping Functions **************************************/

    /**
     * @dev Pause the protocol
     **/
    function pause() external onlyProxy onlyAdmin {
        LiquidityManagerStorage(liquidityManagerStorageAddress()).pause();
    }

    /**
     * @dev UnPause the protocol
     **/
    function unPause() external onlyProxy onlyAdmin {
        LiquidityManagerStorage(liquidityManagerStorageAddress()).unPause();
    }

    /**
     * @dev Initializes a reserve, activating it, assigning two deposit tokens and an interest rate strategy
     * Only callable by protocol operator
     * @param _asset The address of the underlying asset of the reserve
     * @param _juniorDepositTokenAddress The address of the junior deposit token that will be assigned to the reserve
     * @param _seniorDepositTokenAddress The address of the senior deposit token that will be assigned to the reserve
     * @param _interestRateStrategyAddress The address of the interest rate strategy contract
     * @param _optimalIncomeRatio The ratio of income ratio
     **/
    function initReserve(
        address _asset,
        address _juniorDepositTokenAddress,
        address _seniorDepositTokenAddress,
        address _interestRateStrategyAddress,
        address _healthStrategyAddress,
        address _loanStrategyAddress,
        uint256 _optimalIncomeRatio
    ) external onlyProxy onlyAdmin {
        require(Address.isContract(_asset), Errors.LM_NOT_CONTRACT);
        LiquidityManagerStorage(liquidityManagerStorageAddress()).initReserve(
            _asset,
            _juniorDepositTokenAddress,
            _seniorDepositTokenAddress,
            _interestRateStrategyAddress,
            _healthStrategyAddress,
            _loanStrategyAddress,
            _optimalIncomeRatio
        );
        emitReserveInitialized(
            _asset,
            _juniorDepositTokenAddress,
            _seniorDepositTokenAddress,
            _interestRateStrategyAddress,
            _healthStrategyAddress,
            _optimalIncomeRatio
        );
    }

    /**
     * @dev Active a reserve for borrowing
     * @param _asset The address of the reserve
     **/
    function activeReserve(address _asset) external onlyProxy onlyAdmin {
        require(Address.isContract(_asset), Errors.LM_NOT_CONTRACT);
        LiquidityManagerStorage(liquidityManagerStorageAddress()).activeReserve(
                _asset
            );
        emitReserveActivated(_asset);
    }

    /************************************** View Functions **************************************/

    function getReserveData(address _asset)
        public
        view
        returns (DataTypes.ReserveData memory)
    {
        require(Address.isContract(_asset), Errors.LM_NOT_CONTRACT);
        return
            LiquidityManagerStorage(liquidityManagerStorageAddress())
                .getReserveData(_asset);
    }

    function getReserveList() external view returns (address[] memory) {
        return
            LiquidityManagerStorage(liquidityManagerStorageAddress())
                .getReserveList();
    }

    function getConfiguration(address _asset)
        public
        view
        returns (DataTypes.ReserveConfigurationMap memory)
    {
        require(Address.isContract(_asset), Errors.LM_NOT_CONTRACT);
        return
            LiquidityManagerStorage(liquidityManagerStorageAddress())
                .getConfiguration(_asset);
    }

    function getFlags(address _asset)
        public
        view
        returns (
            bool,
            bool,
            bool
        )
    {
        return
            LiquidityManagerStorage(liquidityManagerStorageAddress()).getFlags(
                _asset
            );
    }

    function getLiquidityRate(address _asset, ReserveLogic.Tranche _tranche)
        public
        view
        returns (uint256)
    {
        require(Address.isContract(_asset), Errors.LM_NOT_CONTRACT);
        return
            LiquidityManagerStorage(liquidityManagerStorageAddress())
                .getLiquidityRate(_asset, _tranche);
    }

    function _requireCallerAdmin() internal {
        Voyager v = Voyager(voyager);
        AddressResolver addressResolver = v.addressResolver();
        address aclAddress = addressResolver.getAclManager();
        IACLManager aclManager = IACLManager(aclAddress);
        require(
            aclManager.isLiquidityManager(messageSender),
            "Not vault admin"
        );
    }

    /******************************************** Events *******************************************/

    function addressToBytes32(address input) internal pure returns (bytes32) {
        return bytes32(uint256(uint160(input)));
    }

    event ReserveInitialized(
        address indexed _asset,
        address _juniorDepositTokenAddress,
        address _seniorDepositTokenAddress,
        address _stableDebtAddress,
        address _interestRateStrategyAddress,
        address _healthStrategyAddress,
        uint256 _optimalIncomeRatio
    );
    bytes32 internal constant RESERVE_INITIALIZED_SIG =
        keccak256(
            "ReserveInitialized(address,address,address,address,address,uint256)"
        );

    function emitReserveInitialized(
        address _asset,
        address _juniorDepositTokenAddress,
        address _seniorDepositTokenAddress,
        address _interestRateStrategyAddress,
        address _healthStrategyAddress,
        uint256 _optimalIncomeRatio
    ) internal {
        proxy._emit(
            abi.encode(
                _juniorDepositTokenAddress,
                _seniorDepositTokenAddress,
                _interestRateStrategyAddress,
                _healthStrategyAddress,
                _optimalIncomeRatio
            ),
            2,
            RESERVE_INITIALIZED_SIG,
            addressToBytes32(_asset),
            0,
            0
        );
    }

    event ReserveActivated(address indexed _asset);
    bytes32 internal constant RESERVE_ACTIVATED_SIG =
        keccak256("ReserveActivated(address)");

    function emitReserveActivated(address _asset) internal {
        bytes memory data;
        proxy._emit(
            data,
            2,
            RESERVE_ACTIVATED_SIG,
            addressToBytes32(_asset),
            0,
            0
        );
    }
}
","keccak256":"0x6172bdffffbbba982ecb659eb138f9c11acfb892f29a704856859adba0de9f45","license":"GPL-3.0"},"contracts/component/loan/LoanManager.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {SafeMath} from "@openzeppelin/contracts/utils/math/SafeMath.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {LiquidityManagerStorage} from "../../component/shared/storage/LiquidityManagerStorage.sol";
import {Proxyable} from "../../libraries/proxy/Proxyable.sol";
import {Errors} from "../../libraries/helpers/Errors.sol";
import {WadRayMath} from "../../libraries/math/WadRayMath.sol";
import {DataTypes} from "../../libraries/types/DataTypes.sol";
import {IVoyagerComponent} from "../../interfaces/IVoyagerComponent.sol";
import {IMessageBus} from "../../interfaces/IMessageBus.sol";
import {IHealthStrategy} from "../../interfaces/IHealthStrategy.sol";
import {IReserveInterestRateStrategy} from "../../interfaces/IReserveInterestRateStrategy.sol";
import {ILoanStrategy} from "../../interfaces/ILoanStrategy.sol";
import {IVault} from "../../interfaces/IVault.sol";
import {IVToken} from "../../interfaces/IVToken.sol";
import {ILoanManager} from "../../interfaces/ILoanManager.sol";
import {Voyager} from "../Voyager.sol";

contract LoanManager is Proxyable, ILoanManager {
    using SafeMath for uint256;
    using WadRayMath for uint256;
    using SafeERC20 for IERC20;

    constructor(address payable _proxy, address payable _voyager)
        Proxyable(_proxy)
    {
        voyager = Voyager(_voyager);
    }

    struct ExecuteBorrowParams {
        address asset;
        address user;
        uint256 amount;
        uint256 term;
        uint256 epoch;
        uint256 liquidityRate;
        uint256 borrowRate;
    }

    function borrow(
        address _user,
        address _asset,
        uint256 _amount,
        address payable _vault,
        uint256 _grossAssetValue
    ) external requireNotPaused onlyProxy {
        ExecuteBorrowParams memory executeBorrowParams;
        // todo use min security deposit
        require(_amount >= 1e19, Errors.LOM_INVALID_AMOUNT);

        // 0. check if the user owns the vault
        require(voyager.getVault(_user) == _vault, Errors.LOM_NOT_VAULT_OWNER);

        // 1. check if pool liquidity is sufficient
        DataTypes.ReserveData memory reserveData = voyager.getReserveData(
            _asset
        );

        uint256 availableSeniorLiquidity = IERC20(_asset).balanceOf(
            reserveData.seniorDepositTokenAddress
        );
        require(
            availableSeniorLiquidity >= _amount,
            Errors.LOM_RESERVE_NOT_SUFFICIENT
        );

        // 2. check HF
        //        IHealthStrategy healthStrategy = IHealthStrategy(
        //            reserveData.healthStrategyAddress
        //        );

        // 3. check credit limit
        uint256 availableCreditLimit = voyager.getAvailableCredit(
            _user,
            _asset
        );

        require(
            availableCreditLimit >= _amount,
            Errors.LOM_CREDIT_NOT_SUFFICIENT
        );

        // 4. update debt logic
        executeBorrowParams.term = ILoanStrategy(
            reserveData.loanStrategyAddress
        ).getTerm();
        executeBorrowParams.epoch = ILoanStrategy(
            reserveData.loanStrategyAddress
        ).getEpoch();

        LiquidityManagerStorage lms = LiquidityManagerStorage(
            liquidityManagerStorageAddress()
        );

        // 5. update liquidity index and interest rate
        DataTypes.BorrowStat memory borrowStat = lms.getBorrowStat(_asset);
        (
            executeBorrowParams.liquidityRate,
            executeBorrowParams.borrowRate
        ) = IReserveInterestRateStrategy(
            reserveData.interestRateStrategyAddress
        ).calculateInterestRates(
                _asset,
                reserveData.seniorDepositTokenAddress,
                0,
                _amount,
                borrowStat.totalDebt,
                borrowStat.avgBorrowRate
            );
        lms.updateStateOnBorrow(
            _asset,
            _amount,
            borrowStat.totalDebt.add(borrowStat.totalInterest),
            executeBorrowParams.borrowRate
        );

        lms.insertDebt(
            _asset,
            _vault,
            _amount,
            executeBorrowParams.term,
            executeBorrowParams.epoch,
            executeBorrowParams.borrowRate
        );

        IVToken(reserveData.seniorDepositTokenAddress).transferUnderlyingTo(
            _vault,
            _amount
        );
    }

    function repay(
        address _user,
        address _asset,
        uint256 _drawDown,
        address payable _vault
    ) external requireNotPaused onlyProxy {
        // 0. check if the user owns the vault
        require(voyager.getVault(_user) == _vault, Errors.LOM_NOT_VAULT_OWNER);

        LiquidityManagerStorage lms = LiquidityManagerStorage(
            liquidityManagerStorageAddress()
        );

        // 1. check draw down to get principal and interest
        uint256 principal;
        uint256 interest;
        (principal, interest) = lms.getPMT(_asset, _vault, _drawDown);
        require(principal.add(interest) != 0, Errors.LOM_INVALID_DEBT);

        // 2. update liquidity index and interest rate
        DataTypes.BorrowStat memory borrowStat = lms.getBorrowStat(_asset);
        uint256 totalDebt = borrowStat.totalDebt.add(borrowStat.totalInterest);
        uint256 avgBorrowRate = borrowStat.avgBorrowRate;
        lms.updateStateOnRepayment(
            _asset,
            principal.add(interest),
            totalDebt,
            avgBorrowRate
        );

        // 3. update repay data
        lms.repay(_asset, _vault, _drawDown, principal, interest);

        // 4. transfer underlying asset
        DataTypes.ReserveData memory reserveData = voyager.getReserveData(
            _asset
        );

        IERC20(_asset).safeTransferFrom(
            _user,
            reserveData.seniorDepositTokenAddress,
            principal.add(interest)
        );
    }

    function getVaultDebt(address _reserve, address _vault)
        external
        view
        returns (uint256, uint256)
    {
        LiquidityManagerStorage lms = LiquidityManagerStorage(
            liquidityManagerStorageAddress()
        );
        return lms.getVaultDebt(_reserve, _vault);
    }

    function getDrawDownList(address _reserve, address _vault)
        external
        view
        returns (uint256, uint256)
    {
        LiquidityManagerStorage lms = LiquidityManagerStorage(
            liquidityManagerStorageAddress()
        );
        return lms.getDrawDownList(_reserve, _vault);
    }

    function getDrawDownDetail(
        address _reserve,
        address _vault,
        uint256 _drawDownId
    ) external view returns (DataTypes.DebtDetail memory) {
        LiquidityManagerStorage lms = LiquidityManagerStorage(
            liquidityManagerStorageAddress()
        );
        return lms.getDrawDownDetail(_reserve, _vault, _drawDownId);
    }

    /// @notice Returns the total outstanding principal debt for a particular underlying asset pool
    /// @param underlyingAsset the address of the underlying reserve asset
    /// @return The total outstanding principal owed to depositors.
    function principalBalance(address underlyingAsset)
        external
        view
        returns (uint256)
    {
        DataTypes.BorrowStat memory borrowState = LiquidityManagerStorage(
            liquidityManagerStorageAddress()
        ).getBorrowStat(underlyingAsset);
        return borrowState.totalDebt;
    }

    /// @notice Returns the total outstanding interest debt for a particular underlying asset pool
    /// @param underlyingAsset the address of the underlying reserve asset
    /// @return The total outstanding interest owed to depositors.
    function interestBalance(address underlyingAsset)
        external
        view
        returns (uint256)
    {
        DataTypes.BorrowStat memory borrowState = LiquidityManagerStorage(
            liquidityManagerStorageAddress()
        ).getBorrowStat(underlyingAsset);
        return borrowState.totalInterest;
    }
}
