pragma solidity ^0.8.9;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {Proxyable} from "./Proxyable.sol";
import {IACLManager} from "../../interfaces/IACLManager.sol";

contract Proxy is Ownable {
    Proxyable public target;

    event TargetUpdated(Proxyable newTarget);

    modifier onlyTarget() {
        require(Proxyable(msg.sender) == target, "Must be proxy target");
        _;
    }

    modifier onlyAuthorized() {
        _onlyAuthorized();
        _;
    }

    function setTarget(Proxyable _target) external onlyOwner {
        target = _target;
        emit TargetUpdated(_target);
    }

    function _emit(
        bytes calldata callData,
        uint256 numTopics,
        bytes32 topic1,
        bytes32 topic2,
        bytes32 topic3,
        bytes32 topic4
    ) external onlyTarget {
        uint256 size = callData.length;
        bytes memory _callData = callData;

        assembly {
            /* The first 32 bytes of callData contain its length (as specified by the abi).
             * Length is assumed to be a uint256 and therefore maximum of 32 bytes
             * in length. It is also leftpadded to be a multiple of 32 bytes.
             * This means moving call_data across 32 bytes guarantees we correctly access
             * the data itself. */
            switch numTopics
            case 0 {
                log0(add(_callData, 32), size)
            }
            case 1 {
                log1(add(_callData, 32), size, topic1)
            }
            case 2 {
                log2(add(_callData, 32), size, topic1, topic2)
            }
            case 3 {
                log3(add(_callData, 32), size, topic1, topic2, topic3)
            }
            case 4 {
                log4(add(_callData, 32), size, topic1, topic2, topic3, topic4)
            }
        }
    }

    // solhint-disable no-complex-fallback
    fallback() external payable onlyAuthorized {
        // Mutable call setting Proxyable.messageSender as this is using call not delegatecall
        target.setMessageSender(msg.sender);

        assembly {
            let free_ptr := mload(0x40)
            calldatacopy(free_ptr, 0, calldatasize())

            /* We must explicitly forward ether to the underlying contract as well. */
            let result := call(
                gas(),
                sload(target.slot),
                callvalue(),
                free_ptr,
                calldatasize(),
                0,
                0
            )
            returndatacopy(free_ptr, 0, returndatasize())

            if iszero(result) {
                revert(free_ptr, returndatasize())
            }
            return(free_ptr, returndatasize())
        }
    }

    function _onlyAuthorized() public {
        address voyage = address(target.voyager());
        IACLManager aclManager = IACLManager(
            target.voyager().addressResolver().getAclManager()
        );
        require(
            msg.sender == target.voyager().addressResolver().getVoyage() ||
                aclManager.isLiquidityManager(msg.sender) ||
                aclManager.isLoanManager(msg.sender),
            "Voyager or admin only function"
        );
    }
}
","keccak256":"0x53f55aed31f24cdfac5e3d07862a0fdaa259935f94fa09cc7cfd6f7f2b184020","license":"GPL-3.0"},"contracts/libraries/proxy/Proxyable.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {Proxy} from "./Proxy.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {IVoyagerComponent} from "../../interfaces/IVoyagerComponent.sol";

abstract contract Proxyable is Ownable, IVoyagerComponent {
    /* The proxy this contract exists behind. */
    Proxy public proxy;

    /* The caller of the proxy, passed through to this contract.
     * Note that every function using this member must apply the onlyProxy or
     * optionalProxy modifiers, otherwise their invocations can use stale values. */
    address public messageSender;

    modifier onlyProxy() {
        _onlyProxy();
        _;
    }

    modifier optionalProxy_onlyOwner() {
        _optionalProxy_onlyOwner();
        _;
    }

    event ProxyUpdated(address proxyAddress);

    constructor(address payable _proxy) internal {
        // This contract is abstract, and thus cannot be instantiated directly
        require(owner() != address(0), "Owner must be set");

        proxy = Proxy(_proxy);
        emit ProxyUpdated(_proxy);
    }

    function _onlyProxy() private view {
        require(msg.sender == address(proxy), "Only the proxy can call");
    }

    function setProxy(address payable _proxy) external onlyOwner {
        proxy = Proxy(_proxy);
        emit ProxyUpdated(_proxy);
    }

    function setMessageSender(address sender) external onlyProxy {
        messageSender = sender;
    }

    // solhint-disable-next-line func-name-mixedcase
    function _optionalProxy_onlyOwner() private {
        if (msg.sender != address(proxy) && messageSender != msg.sender) {
            messageSender = msg.sender;
        }
        require(messageSender == owner(), "Owner only function");
    }
}
