// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

library DataTypes {
    enum Tranche {
        JUNIOR,
        SENIOR
    }

    struct ReserveData {
        //stores the reserve configuration
        ReserveConfigurationMap configuration;
        // for calculating overall interested accumulated
        // then split it into two indexs base on two allocations
        uint256 currentOverallLiquidityRate;
        // Expressed in ray
        uint256 currentSeniorIncomeAllocation;
        // Expressed in ray
        uint256 currentJuniorIncomeAllocation;
        uint256 juniorLiquidityIndex;
        uint256 seniorLiquidityIndex;
        uint256 totalBorrows;
        uint256 currentBorrowRate;
        // Expressed in ray
        uint256 securityRequirement;
        //the decimals of the reserve asset
        uint256 decimals;
        address interestRateStrategyAddress;
        address healthStrategyAddress;
        address juniorDepositTokenAddress;
        address seniorDepositTokenAddress;
        address stableDebtAddress;
        uint40 juniorLastUpdateTimestamp;
        uint40 seniorLastUpdateTimestamp;
    }

    struct ReserveConfigurationMap {
        //bit 0-15: Liquidate bonus
        //bit 16-23: Decimals
        //bit 24: Reserve is active
        //bit 25: reserve is frozen
        //bit 26: borrowing is enabled
        //bit 27-30: reserved
        //bit 31-46: reserve factor
        //bit 47-62: lock up period in seconds
        uint256 data;
    }

    struct FungibleTokenData {
        string symbol;
        address tokenAddress;
    }

    struct BorrowData {
        uint256 drawDownNumber;
        uint256 totalDebt;
        mapping(uint256 => DrawDown) drawDowns;
        mapping(uint256 => Repayment) repayments;
    }

    struct DrawDown {
        uint256 amount;
        uint256 tenure;
        uint40 timestamp;
        uint256 borrowRate;
        Repayment repayment;
    }

    struct Repayment {
        uint256 totalPaid;
        // tenure => amount
        // todo wrapper this in the future
        mapping(uint256 => uint256) repayment;
    }

    // tmp struct to avoid stack too long

    struct DepositAndDebt {
        uint256 juniorDepositAmount;
        uint256 seniorDepositAmount;
        uint256 totalDebt;
    }

    struct HealthRiskParameter {
        uint256 securityDeposit;
        uint256 currentBorrowRate;
        uint256 compoundedDebt;
        uint256 grossAssetValue;
        uint256 aggregateOptimalRepaymentRate;
        uint256 aggregateActualRepaymentRate;
    }
}

contracts/libraries/logic/ReserveLogic.s.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import 'openzeppelin-solidity/contracts/utils/math/SafeMath.sol';
import '../math/WadRayMath.sol';
import '../math/MathUtils.sol';
import '../types/DataTypes.sol';
import '../helpers/Errors.sol';
import '../../interfaces/IDebtToken.sol';
import '../../component/liquiditymanager/DefaultReserveInterestRateStrategy.sol';

/**
 * @title ReserveLogic library
 * @author Voyager
 * @notice Implements the logic to update the reserves state
 **/
library ReserveLogic {
    using SafeMath for uint256;
    using WadRayMath for uint256;

    using ReserveLogic for DataTypes.ReserveData;

    enum Tranche {
        JUNIOR,
        SENIOR
    }

    /**
     * @dev Emitted when the state of a reserve is updated
     * @param asset The address of the underlying asset of the reserve
     * @param liquidityRate The new liquidity rate
     * @param stableBorrowRate The new stable borrow rate
     * @param liquidityIndex The new liquidity index
     **/
    event ReserveDataUpdated(
        address indexed asset,
        uint256 liquidityRate,
        uint256 stableBorrowRate,
        uint256 liquidityIndex
    );

    function init(
        DataTypes.ReserveData storage reserve,
        address _juniorDepositTokenAddress,
        address _seniorDepositTokenAddress,
        uint256 _juniorIncomeAllocation,
        uint256 _seniorIncomeAllocation,
        address _stableDebtAddress,
        address _interestRateStrategyAddress,
        address _healthStrategyAddress
    ) external {
        reserve.juniorLiquidityIndex = WadRayMath.ray();
        reserve.seniorLiquidityIndex = WadRayMath.ray();
        reserve.juniorDepositTokenAddress = _juniorDepositTokenAddress;
        reserve.seniorDepositTokenAddress = _seniorDepositTokenAddress;
        //reserve.currentOverallLiquidityRate = WadRayMath.ray();
        reserve.currentJuniorIncomeAllocation = _juniorIncomeAllocation;
        reserve.currentSeniorIncomeAllocation = _seniorIncomeAllocation;
        reserve.stableDebtAddress = _stableDebtAddress;
        reserve.interestRateStrategyAddress = _interestRateStrategyAddress;
        reserve.healthStrategyAddress = _healthStrategyAddress;
    }

    function updateState(
        DataTypes.ReserveData storage reserve,
        Tranche _tranche
    ) public {
        _updateIndexes(reserve, _tranche);
    }

    function getLiquidityRate(
        DataTypes.ReserveData storage reserve,
        Tranche _tranche
    ) public view returns (uint256) {
        return reserve._getLiquidityRate(_tranche);
    }

    struct UpdateInterestRatesLocalVars {
        address stableDebtTokenAddress;
        uint256 availableLiquidity;
        uint256 totalStableDebt;
        uint256 newLiquidityRate;
        uint256 newStableRate;
        uint256 avgStableRate;
    }

    function updateInterestRates(
        DataTypes.ReserveData storage _reserve,
        address _reserveAddress,
        uint256 _juniorLiquidityAdded,
        uint256 _juniorLiquidityTaken,
        uint256 _seniorLiquidityAdded,
        uint256 _seniorLiquidityTaken
    ) public {
        UpdateInterestRatesLocalVars memory vars;

        vars.stableDebtTokenAddress = _reserve.stableDebtAddress;
        uint256 liquidityAdded = _juniorLiquidityAdded.add(
            _seniorLiquidityAdded
        );
        uint256 liquidityTaken = _juniorLiquidityTaken.add(
            _seniorLiquidityTaken
        );

        (vars.totalStableDebt, vars.avgStableRate) = IStableDebtToken(
            _reserve.stableDebtAddress
        ).getTotalSupplyAndAvgRate();

        (
            vars.newLiquidityRate,
            vars.newStableRate
        ) = IReserveInterestRateStrategy(_reserve.interestRateStrategyAddress)
            .calculateInterestRates(
                _reserveAddress,
                _reserve.juniorDepositTokenAddress,
                _reserve.seniorDepositTokenAddress,
                liquidityAdded,
                liquidityTaken,
                _reserve.totalBorrows,
                vars.avgStableRate
            );
        require(
            vars.newLiquidityRate <= type(uint128).max,
            Errors.RL_LIQUIDITY_RATE_OVERFLOW
        );
        require(
            vars.newStableRate <= type(uint128).max,
            Errors.RL_STABLE_BORROW_RATE_OVERFLOW
        );
        _reserve.currentOverallLiquidityRate = vars.newLiquidityRate;
        _reserve.currentBorrowRate = vars.newStableRate;

        emit ReserveDataUpdated(
            _reserveAddress,
            vars.newLiquidityRate,
            vars.newStableRate,
            vars.newLiquidityRate
        );
    }

    function getNormalizedIncome(
        DataTypes.ReserveData storage reserve,
        Tranche _tranche
    ) internal view returns (uint256) {
        uint40 timestamp;
        uint256 liquidityIndex;
        if (_tranche == Tranche.JUNIOR) {
            timestamp = reserve.juniorLastUpdateTimestamp;
            liquidityIndex = reserve.juniorLiquidityIndex;
        } else {
            timestamp = reserve.seniorLastUpdateTimestamp;
            liquidityIndex = reserve.seniorLiquidityIndex;
        }

        //solium-disable-next-line
        if (timestamp == uint40(block.timestamp)) {
            return liquidityIndex;
        }

        uint256 cumulated = MathUtils
            .calculateLinearInterest(
                reserve._getLiquidityRate(_tranche),
                timestamp
            )
            .rayMul(liquidityIndex);
        return cumulated;
    }

    function _getLiquidityRate(
        DataTypes.ReserveData storage reserve,
        Tranche _tranche
    ) internal view returns (uint256) {
        uint256 totalAllocationInRay = reserve
            .currentJuniorIncomeAllocation
            .add(reserve.currentSeniorIncomeAllocation);
        if (_tranche == Tranche.JUNIOR) {
            return
                reserve.currentOverallLiquidityRate.rayMul(
                    reserve.currentJuniorIncomeAllocation.rayDiv(
                        totalAllocationInRay
                    )
                );
        } else {
            return
                reserve.currentOverallLiquidityRate.rayMul(
                    reserve.currentSeniorIncomeAllocation.rayDiv(
                        totalAllocationInRay
                    )
                );
        }
    }

    function _updateIndexes(
        DataTypes.ReserveData storage reserve,
        Tranche _tranche
    ) internal {
        if (_tranche == Tranche.JUNIOR) {
            uint256 previousJuniorLiquidityIndex = reserve.juniorLiquidityIndex;
            uint256 lastJuniorUpdatedTimestamp = reserve
                .juniorLastUpdateTimestamp;
            reserve._updateJuniorLiquidityIndex(
                previousJuniorLiquidityIndex,
                uint40(lastJuniorUpdatedTimestamp)
            );
        } else {
            uint256 previousSeniorLiquidityIndex = reserve.seniorLiquidityIndex;
            uint256 lastSeniorUpdatedTimestamp = reserve
                .seniorLastUpdateTimestamp;
            reserve._updateSeniorLiquidityIndex(
                previousSeniorLiquidityIndex,
                uint40(lastSeniorUpdatedTimestamp)
            );
        }
    }

    function _updateJuniorLiquidityIndex(
        DataTypes.ReserveData storage reserve,
        uint256 juniorLiquidityIndex,
        uint40 timestamp
    ) internal returns (uint256) {
        uint256 juniorLiquidityRate = reserve._getLiquidityRate(Tranche.JUNIOR);
        uint256 newJuniorLiquidityIndex = juniorLiquidityIndex;

        // only cumulating if there is any income being produced
        if (juniorLiquidityRate > 0) {
            uint256 cumulatedLiquidityInterest = MathUtils
                .calculateLinearInterest(juniorLiquidityRate, timestamp);
            newJuniorLiquidityIndex = cumulatedLiquidityInterest.rayMul(
                juniorLiquidityIndex
            );
            reserve.juniorLiquidityIndex = newJuniorLiquidityIndex;
        }

        reserve.juniorLastUpdateTimestamp = uint40(block.timestamp);
        return newJuniorLiquidityIndex;
    }

    function _updateSeniorLiquidityIndex(
        DataTypes.ReserveData storage reserve,
        uint256 seniorLiquidityIndex,
        uint40 timestamp
    ) internal returns (uint256) {
        uint256 seniorLiquidityRate = reserve._getLiquidityRate(Tranche.SENIOR);
        uint256 newSeniorLiquidityIndex = seniorLiquidityIndex;

        if (seniorLiquidityRate > 0) {
            uint256 cumulatedLiquidityInterest = MathUtils
                .calculateLinearInterest(seniorLiquidityRate, timestamp);
            newSeniorLiquidityIndex = cumulatedLiquidityInterest.rayMul(
                seniorLiquidityIndex
            );
            reserve.seniorLiquidityIndex = newSeniorLiquidityIndex;
        }
        reserve.seniorLastUpdateTimestamp = uint40(block.timestamp);
        return newSeniorLiquidityIndex;
    }
}

contracts/component/vault/VaultManager.s.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import 'openzeppelin-solidity/contracts/access/AccessControl.sol';
import 'openzeppelin-solidity/contracts/token/ERC20/utils/SafeERC20.sol';
import 'openzeppelin-solidity/contracts/utils/math/SafeMath.sol';
import 'openzeppelin-solidity/contracts/token/ERC20/ERC20.sol';
import 'openzeppelin-solidity/contracts/security/ReentrancyGuard.sol';
import '../../libraries/proxy/Proxyable.sol';
import '../../tokenization/SecurityDepositToken.sol';
import '../../mock/Tus.sol';
import '../../libraries/math/WadRayMath.sol';
import './Vault.sol';
import '../../interfaces/IVaultManager.sol';
import '../../interfaces/IACLManager.sol';
import '../Voyager.sol';
import '../infra/AddressResolver.sol';
import './VaultStorage.sol';

contract VaultManager is ReentrancyGuard, Proxyable, IVaultManager {
    using SafeERC20 for ERC20;
    using WadRayMath for uint256;
    using SafeMath for uint256;

    address public voyager;
    mapping(address => uint256) public maxSecurityDeposit;
    // reserve address => requirement expressed in ray
    mapping(address => uint256) public securityDepositRequirement;

    constructor(address payable _proxy, address _voyager)
        public
        Proxyable(_proxy)
    {
        voyager = _voyager;
    }

    modifier onlyAdmin() {
        _requireCallerAdmin();
        _;
    }

    /************************************** User Functions **************************************/

    /**
     * @dev Create a Vault for user
     * @param _user the address of the player
     **/
    function createVault(address _user)
        external
        onlyProxy
        returns (address vault)
    {
        bytes memory bytecode = type(Vault).creationCode;
        bytes32 salt = keccak256(abi.encodePacked(_user));
        assembly {
            vault := create2(0, add(bytecode, 32), mload(bytecode), salt)
        }
        Vault(vault).initialize(voyager, _user);
        uint256 len = VaultStorage(getVaultStorageAddress()).pushNewVault(
            _user,
            vault
        );
        proxy._emit(
            abi.encode(vault, len),
            2,
            keccak256('VaultCreated(address, address, uint256)'),
            bytes32(abi.encodePacked(_user)),
            0,
            0
        );
    }

    /**
     * @dev Delegate call to Vault's depositSecurity
     * @param _sponsor who actual deposits the reserve into the amount
     * @param _vaultUser user address
     * @param _reserve reserve address
     * @param _amount amount user is willing to deposit
     */
    function depositSecurity(
        address _sponsor,
        address _vaultUser,
        address _reserve,
        uint256 _amount
    ) external onlyProxy {
        address vaultAddress = _getVault(_vaultUser);
        Vault(vaultAddress).depositSecurity(_sponsor, _reserve, _amount);
        proxy._emit(
            abi.encode(_vaultUser, _reserve, _amount),
            2,
            keccak256('SecurityDeposited(address, address, address, uint256)'),
            bytes32(abi.encodePacked(_sponsor)),
            0,
            0
        );
    }

    /**
     * @dev  Delegate call to Vault's redeemSecurity
     * @param _sponsor sponsor address
     * @param _vaultUser user address
     * @param _reserve reserve address
     * @param _amount redeem amount
     **/
    function redeemSecurity(
        address payable _sponsor,
        address _vaultUser,
        address _reserve,
        uint256 _amount
    ) external onlyProxy {
        address vaultAddress = _getVault(_vaultUser);
        Vault(vaultAddress).redeemSecurity(_sponsor, _reserve, _amount);
        proxy._emit(
            abi.encode(_vaultUser, _reserve, _amount),
            2,
            keccak256('SecurityRedeemed(address, address, address, uint256)'),
            bytes32(abi.encodePacked(_sponsor)),
            0,
            0
        );
    }

    // placeholder function
    function slash(
        address _vaultUser,
        address _reserve,
        address payable _to,
        uint256 _amount
    ) public nonReentrant onlyProxy {
        address vaultAddress = _getVault(_vaultUser);
        return Vault(vaultAddress).slash(_reserve, _to, _amount);
    }

    /************************ HouseKeeping Function ******************************/

    /**
     * Init a deployed Vault, ensure it has overlying security deposit token and corresponding staking contract
     * _vaultUser the user/owner of this vault
     * _reserve the underlying asset address e.g. TUS
     **/
    function initVault(address _user, address _reserve) external {
        initSecurityDepositToken(_user, _reserve);
        initStakingContract(_user, _reserve);
    }

    function initSecurityDepositToken(address _vaultUser, address _reserve)
        public
        onlyProxy
        onlyAdmin
    {
        address vaultAddress = _getVault(_vaultUser);
        Vault(vaultAddress).initSecurityDepositToken(_reserve);
        proxy._emit(
            abi.encode(_reserve),
            2,
            keccak256('SecurityDepositTokenInited(address, address)'),
            bytes32(abi.encodePacked(_vaultUser)),
            0,
            0
        );
    }

    /**
     * Init a deployed Vault, ensure it has overlying security deposit token and corresponding staking contract
     * _vaultUser the user/owner of this vault
     * _reserve the underlying asset address e.g. TUS
     **/
    function initStakingContract(address _vaultUser, address _reserve)
        public
        onlyProxy
        onlyAdmin
    {
        address vaultAddress = _getVault(_vaultUser);
        Vault(vaultAddress).initStakingContract(_reserve);
        proxy._emit(
            abi.encode(_reserve),
            2,
            keccak256('StakingContractInited(address, address)'),
            bytes32(abi.encodePacked(_vaultUser)),
            0,
            0
        );
    }

    /**
     * @dev Set max security deposit for _reserve
     * @param _reserve reserve address
     * @param _amount max amount sponsor can deposit
     */
    function setMaxSecurityDeposit(address _reserve, uint256 _amount)
        external
        onlyProxy
        onlyAdmin
    {
        maxSecurityDeposit[_reserve] = _amount;
        proxy._emit(
            abi.encode(_amount),
            2,
            keccak256('MaxSecurityDepositUpdated(address, uint256)'),
            bytes32(abi.encodePacked(_reserve)),
            0,
            0
        );
    }

    /**
     * @dev Remove max security deposit for _reserve
     * @param _reserve reserve address
     */
    function removeMaxSecurityDeposit(address _reserve)
        external
        onlyProxy
        onlyAdmin
    {
        delete maxSecurityDeposit[_reserve];
        proxy._emit(
            abi.encode(_reserve),
            2,
            keccak256('MaxSecurityDepositDeleted(address)'),
            bytes32(abi.encodePacked(_reserve)),
            0,
            0
        );
    }

    /**
     * @dev Update the security deposit requirement
     * @param _reserve reserve address
     * @param _requirement expressed in Ray
     */
    function updateSecurityDepositRequirement(
        address _reserve,
        uint256 _requirement
    ) external onlyProxy onlyAdmin {
        securityDepositRequirement[_reserve] = _requirement;
        proxy._emit(
            abi.encode(_requirement),
            2,
            keccak256('SecurityDepositRequirementSet(address, uint256)'),
            bytes32(abi.encodePacked(_reserve)),
            0,
            0
        );
    }

    /**
     * @dev Remove security deposit
     * @param _reserve reserve address
     */
    function removeSecurityDepositRequirement(address _reserve)
        external
        onlyProxy
        onlyAdmin
    {
        delete securityDepositRequirement[_reserve];
        proxy._emit(
            abi.encode(_reserve),
            2,
            keccak256('SecurityDepositRequirementDeleted(address)'),
            bytes32(abi.encodePacked(_reserve)),
            0,
            0
        );
    }

    /************************************** View Functions **************************************/

    function getSecurityDepositRequirement(address _reserve)
        external
        view
        returns (uint256)
    {
        return securityDepositRequirement[_reserve];
    }

    function getMaxSecurityDeposit(address _reserve)
        external
        view
        onlyProxy
        returns (uint256)
    {
        return maxSecurityDeposit[_reserve];
    }

    function underlyingBalance(
        address _vaultUser,
        address _reserve,
        address _sponsor
    ) public view returns (uint256) {
        address vaultAddress = _getVault(_vaultUser);
        return Vault(vaultAddress).underlyingBalance(_sponsor, _reserve);
    }

    /**
     * @dev Get available credit
     * @param _user user address
     * @param _reserve reserve address
     **/
    function getAvailableCredit(address _user, address _reserve)
        public
        view
        returns (uint256)
    {
        uint256 creditLimit = getCreditLimit(_user, _reserve);
        uint256 accumulatedDebt = Vault(_getVault(_user)).getTotalDebt();
        return creditLimit - accumulatedDebt;
    }

    /**
     * @dev Get credit limit for a specific reserve
     * @param _user user address
     * @return _reserve reserve address
     **/
    function getCreditLimit(address _user, address _reserve)
        public
        view
        returns (uint256)
    {
        uint256 currentSecurityDeposit = _getSecurityDeposit(_user, _reserve);
        uint256 securityDepositRequirement = securityDepositRequirement[
            _reserve
        ];
        require(
            securityDepositRequirement != 0,
            'security deposit requirement cannot be 0'
        );
        uint256 creditLimitInRay = currentSecurityDeposit.wadToRay().rayDiv(
            securityDepositRequirement
        );
        return creditLimitInRay.rayToWad();
    }

    function getSecurityDeposit(address _user, address _reserve)
        external
        view
        returns (uint256)
    {
        return _getSecurityDeposit(_user, _reserve);
    }

    function getVaultStorageAddress() private view returns (address) {
        Voyager v = Voyager(voyager);
        address resolver = v.getAddressResolverAddress();
        return AddressResolver(resolver).getAddress(v.getVaultStorageName());
    }

    function getSecurityDepositTokenAddress(address vault)
        private
        view
        returns (address)
    {
        return Vault(vault).getSecurityDepositTokenAddress();
    }

    /**
     * @dev Get existing Vault contract address for user
     * @param _user the address of the player
     * @return Vault address
     **/
    function getVault(address _user) external view returns (address) {
        return _getVault(_user);
    }

    function eligibleAmount(
        address _vaultUser,
        address _reserve,
        address _sponsor
    ) public view returns (uint256) {
        address vaultAddress = _getVault(_vaultUser);
        return Vault(vaultAddress).eligibleAmount(_reserve, _sponsor);
    }

    /************************************** Private Functions **************************************/

    function _getSecurityDeposit(address _user, address _reserve)
        internal
        view
        returns (uint256)
    {
        address vaultAddress = _getVault(_user);
        uint256 currentSecurityDeposit = Vault(vaultAddress)
            .getCurrentSecurityDeposit(_reserve);
        return currentSecurityDeposit;
    }

    function _getVault(address _user) internal view returns (address) {
        return VaultStorage(getVaultStorageAddress()).getVaultAddress(_user);
    }

    function _requireCallerAdmin() internal {
        Voyager v = Voyager(voyager);
        IACLManager aclManager = IACLManager(
            v.addressResolver().getAddress(v.getACLManagerName())
        );
        require(aclManager.isVaultManager(messageSender), 'Not vault admin');
    }
}

