contract SeniorDepositToken is BaseDepositToken {
    using SafeMath for uint256;

    constructor(
        address _voyager,
        ERC20 _underlyingAsset,
        string memory _name,
        string memory _symbol
    ) BaseDepositToken(_voyager, _underlyingAsset, _name, _symbol) {}

    function totalAssets() public view override returns (uint256) {
        uint256 underlyingBalance = asset.balanceOf(address(this));
        console.log("underlyingBalance: %s", underlyingBalance);
        uint256 outstandingPrincipal = LoanFacet(voyager).principalBalance(
            address(asset)
        );
        console.log("outstandingPrincipal: %s", outstandingPrincipal);
        return underlyingBalance + outstandingPrincipal - totalUnbonding;
    }
}

contracts/tokenization/base/BaseDepositT.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {Context} from "@openzeppelin/contracts/utils/Context.sol";
import {SafeMath} from "@openzeppelin/contracts/utils/math/SafeMath.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {ERC20} from "@rari-capital/solmate/src/tokens/ERC20.sol";
import {SafeTransferLib} from "@rari-capital/solmate/src/utils/SafeTransferLib.sol";
import {Errors} from "../../libraries/helpers/Errors.sol";
import {IVToken} from "../../interfaces/IVToken.sol";
import {AddressResolver} from "../../component/infra/AddressResolver.sol";

abstract contract BaseDepositToken is Context, IVToken {
    using SafeMath for uint256;
    using SafeTransferLib for ERC20;

    address internal immutable voyager;
    // user address => timestamp => amount
    mapping(address => mapping(uint256 => uint256)) private withdrawals;

    // user address => timestamp array
    mapping(address => uint256[]) private pendingTimestamp;

    uint256 public override totalUnbonding;

    uint256 public cooldown = 7 days;

    event Claim(address receiver, uint256 amount);

    modifier onlyAdmin() {
        require(
            _msgSender() == voyager,
            Errors.CT_CALLER_MUST_BE_LIQUIDITY_MANAGER_POOL
        );
        _;
    }

    constructor(
        address _voyager,
        ERC20 _underlyingAsset,
        string memory _name,
        string memory _symbol
    ) IVToken(_underlyingAsset, _name, _symbol) {
        voyager = _voyager;
    }

    function withdraw(
        uint256 _amount,
        address _receiver,
        address _owner
    ) public override returns (uint256 shares) {
        shares = previewWithdraw(_amount); // No need to check for rounding error, previewWithdraw rounds up.

        if (msg.sender != _owner) {
            uint256 allowed = allowance[_owner][msg.sender]; // Saves gas for limited approvals.

            if (allowed != type(uint256).max)
                allowance[_owner][msg.sender] = allowed - shares;
        }

        beforeWithdraw(_amount, shares);

        _burn(_owner, shares);
        pushWithdraw(_owner, _amount);

        emit Withdraw(msg.sender, _receiver, _owner, _amount, shares);
    }

    function claim(uint256 _index) public override {
        uint256 amount = popWithdraw(msg.sender, _index);
        require(
            asset.balanceOf(address(this)) >= amount,
            "Insufficient liquidity available"
        );
        asset.safeTransfer(msg.sender, amount);
    }

    function transferUnderlyingTo(address _target, uint256 _amount)
        public
        override
        onlyAdmin
    {
        asset.safeTransfer(_target, _amount);
    }

    function pushWithdraw(address _user, uint256 _amount) internal {
        require(withdrawals[_user][block.timestamp] == 0, "invalid withdraw");
        withdrawals[_user][block.timestamp] = _amount;
        pendingTimestamp[_user].push(block.timestamp);
        totalUnbonding += _amount;
    }

    function popWithdraw(address _user, uint256 _index)
        internal
        returns (uint256)
    {
        uint256[] storage times = pendingTimestamp[_user];
        require(_index < times.length, "invalid index");
        uint256 ts = times[_index];
        require(block.timestamp - ts > cooldown, "cool down error");

        uint256 last = times[times.length - 1];
        times[_index] = last;
        times.pop();

        uint256 withdrawable = withdrawals[_user][ts];
        delete withdrawals[_user][ts];
        totalUnbonding -= withdrawable;
        return withdrawable;
    }

    function unbonding(address _user)
        public
        view
        override
        returns (uint256[] memory, uint256[] memory)
    {
        uint256[] memory times = pendingTimestamp[_user];
        uint256[] memory amounts = new uint256[](times.length);

        for (uint256 i = 0; i < times.length; i++) {
            amounts[i] = withdrawals[_user][times[i]];
        }

        return (times, amounts);
    }
}

contracts/component/facets/LiquidityFace.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {SafeMath} from "@openzeppelin/contracts/utils/math/SafeMath.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Errors} from "../../libraries/helpers/Errors.sol";
import {WadRayMath} from "../../libraries/math/WadRayMath.sol";
import {IVToken} from "../../interfaces/IVToken.sol";
import {JuniorDepositToken} from "../../tokenization/JuniorDepositToken.sol";
import {SeniorDepositToken} from "../../tokenization/SeniorDepositToken.sol";
import {LibAppStorage, AppStorage, Storage, Tranche, ReserveData, BorrowState, ReserveConfigurationMap} from "../../libraries/LibAppStorage.sol";
import {ReserveConfiguration} from "../../libraries/configuration/ReserveConfiguration.sol";
import {LibLiquidity} from "../../libraries/LibLiquidity.sol";
import {PeripheryPayments} from "../../libraries/utils/PeripheryPayments.sol";
import "hardhat/console.sol";

contract LiquidityFacet is Storage, PeripheryPayments {
    using LibLiquidity for ReserveData;
    using ReserveConfiguration for ReserveConfigurationMap;
    using WadRayMath for uint256;
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    event ReserveInitialized(
        address indexed _asset,
        address _juniorDepositTokenAddress,
        address _seniorDepositTokenAddress,
        address _interestRateStrategyAddress,
        uint256 _optimalIncomeRatio
    );
    event ReserveActivated(address indexed _asset);
    event Deposit(
        address indexed asset,
        address indexed user,
        Tranche indexed tranche,
        uint256 amount
    );
    event Withdraw(
        address indexed asset,
        address indexed user,
        Tranche indexed tranche,
        uint256 amount
    );

    /* ----------------------------- admin interface ---------------------------- */

    function initReserve(
        address _asset,
        address _juniorDepositTokenAddress,
        address _seniorDepositTokenAddress,
        address _interestRateStrategyAddress,
        address _loanStrategyAddress,
        uint256 _optimalIncomeRatio
    ) external onlyAdmin {
        require(Address.isContract(_asset), Errors.LM_NOT_CONTRACT);
        ReserveData storage reserveData = LibLiquidity.getReserveData(_asset);
        require(reserveData.initialized == false, "cannot initialize twice");
        reserveData.init(
            _juniorDepositTokenAddress,
            _seniorDepositTokenAddress,
            _interestRateStrategyAddress,
            _loanStrategyAddress,
            _optimalIncomeRatio
        );
        s._reserveList[s._reservesCount] = _asset;
        s._reservesCount++;
        emit ReserveInitialized(
            _asset,
            _juniorDepositTokenAddress,
            _seniorDepositTokenAddress,
            _interestRateStrategyAddress,
            _optimalIncomeRatio
        );
    }

    function activateReserve(address _asset) external onlyAdmin {
        require(Address.isContract(_asset), Errors.LM_NOT_CONTRACT);
        ReserveConfigurationMap memory config = LibLiquidity.getConfiguration(
            _asset
        );
        config.setActive(true);
        s._reserves[_asset].configuration.data = config.data;
        emit ReserveActivated(_asset);
    }

    /* ----------------------------- user interface ----------------------------- */

    function deposit(
        address _asset,
        Tranche _tranche,
        uint256 _amount,
        address _user
    ) external {
        ReserveData memory reserve = s._reserves[_asset];
        BorrowState memory borrowState = s._borrowState[_asset];
        uint256 totalDebt = borrowState.totalDebt.add(
            borrowState.totalInterest
        );
        uint256 avgBorrowRate = borrowState.avgBorrowRate;
        LibLiquidity.updateStateOnDeposit(
            _asset,
            _tranche,
            _amount,
            totalDebt,
            avgBorrowRate
        );

        IVToken vToken = _tranche == Tranche.JUNIOR
            ? IVToken(reserve.juniorDepositTokenAddress)
            : IVToken(reserve.seniorDepositTokenAddress);
        // transfer the underlying tokens to liquidity manager, then do deposit.
        pullToken(vToken.asset(), _amount, _user, address(this));
        vToken.deposit(_amount, _user);
        emit Deposit(_asset, _user, _tranche, _amount);
    }

    function withdraw(
        address _asset,
        Tranche _tranche,
        uint256 _amount,
        address payable _user
    ) external {
        ReserveData memory reserve = s._reserves[_asset];
        IVToken vToken = Tranche.JUNIOR == _tranche
            ? IVToken(reserve.juniorDepositTokenAddress)
            : IVToken(reserve.seniorDepositTokenAddress);
        uint256 userBalance = vToken.maxWithdraw(_user);
        uint256 amountToWithdraw = _amount;
        if (_amount == type(uint256).max) {
            amountToWithdraw = userBalance;
        }
        console.log("amount to withdraw: %s", amountToWithdraw);
        BorrowState memory borrowState = s._borrowState[_asset];
        uint256 totalDebt = borrowState.totalDebt.add(
            borrowState.totalInterest
        );
        console.log("total debt: %s", totalDebt);
        uint256 avgBorrowRate = borrowState.avgBorrowRate;
        console.log("avg borrow rate: %s", borrowState.avgBorrowRate);
        IVToken(vToken).withdraw(_amount, _user, _user);
        console.log("withdrew");
        LibLiquidity.updateStateOnWithdraw(
            _asset,
            _tranche,
            amountToWithdraw,
            totalDebt,
            avgBorrowRate
        );

        emit Withdraw(_asset, _user, _tranche, _amount);
    }

    /* ---------------------------------- views --------------------------------- */

    function balance(
        address _reserve,
        address _user,
        Tranche _tranche
    ) public view returns (uint256) {
        return LibLiquidity.balance(_reserve, _user, _tranche);
    }

    function liquidityRate(address _asset, Tranche _tranche)
        public
        view
        returns (uint256)
    {
        return LibLiquidity.getLiquidityRate(_asset, _tranche);
    }

    function unbonding(
        address _reserve,
        address _user,
        Tranche _tranche
    ) public view returns (uint256) {
        return LibLiquidity.unbonding(_reserve, _user, _tranche);
    }

    function utilizationRate(address _reserve) external view returns (uint256) {
        ReserveData memory reserve = LibLiquidity.getReserveData(_reserve);
        BorrowState storage borrowState = s._borrowState[_reserve];
        uint256 totalDebt = borrowState.totalDebt.add(
            borrowState.totalInterest
        );

        uint256 totalPendingWithdrawal = IVToken(
            reserve.seniorDepositTokenAddress
        ).totalUnbonding();

        uint256 availableLiquidity = IERC20(_reserve).balanceOf(
            reserve.seniorDepositTokenAddress
        ) - totalPendingWithdrawal;

        return
            totalDebt == 0
                ? 0
                : totalDebt.rayDiv(availableLiquidity.add(totalDebt));
    }

    function getReserveFlags(address _reserve)
        external
        view
        returns (
            bool,
            bool,
            bool
        )
    {
        return LibLiquidity.getFlags(_reserve);
    }
}

