pragma solidity ^0.8.9;

import "./DSAuth.sol";

contract DSRoles is DSAuth, DSAuthority {
    mapping(address => bool) _root_users;
    mapping(address => bytes32) _user_roles;
    mapping(address => mapping(bytes4 => bytes32)) _capability_roles;
    mapping(address => mapping(bytes4 => bool)) _public_capabilities;

    function getUserRoles(address who) public view returns (bytes32) {
        return _user_roles[who];
    }

    function getCapabilityRoles(address code, bytes4 sig)
        public
        view
        returns (bytes32)
    {
        return _capability_roles[code][sig];
    }

    function isUserRoot(address who) public view returns (bool) {
        return _root_users[who];
    }

    function isCapabilityPublic(address code, bytes4 sig)
        public
        view
        returns (bool)
    {
        return _public_capabilities[code][sig];
    }

    function hasUserRole(address who, uint8 role) public view returns (bool) {
        bytes32 roles = getUserRoles(who);
        bytes32 shifted = bytes32(uint256(uint256(2)**uint256(role)));
        return bytes32(0) != roles & shifted;
    }

    function canCall(
        address caller,
        address code,
        bytes4 sig
    ) public view returns (bool) {
        if (isUserRoot(caller) || isCapabilityPublic(code, sig)) {
            return true;
        } else {
            bytes32 has_roles = getUserRoles(caller);
            bytes32 needs_one_of = getCapabilityRoles(code, sig);
            return bytes32(0) != has_roles & needs_one_of;
        }
    }

    function BITNOT(bytes32 input) internal pure returns (bytes32 output) {
        return (input ^ bytes32(type(uint256).max));
    }

    function setRootUser(address who, bool enabled) public auth {
        _root_users[who] = enabled;
    }

    function setUserRole(
        address who,
        uint8 role,
        bool enabled
    ) public auth {
        bytes32 last_roles = _user_roles[who];
        bytes32 shifted = bytes32(uint256(uint256(2)**uint256(role)));
        if (enabled) {
            _user_roles[who] = last_roles | shifted;
        } else {
            _user_roles[who] = last_roles & BITNOT(shifted);
        }
    }

    function setPublicCapability(
        address code,
        bytes4 sig,
        bool enabled
    ) public auth {
        _public_capabilities[code][sig] = enabled;
    }

    function setRoleCapability(
        uint8 role,
        address code,
        bytes4 sig,
        bool enabled
    ) public auth {
        bytes32 last_roles = _capability_roles[code][sig];
        bytes32 shifted = bytes32(uint256(uint256(2)**uint256(role)));
        if (enabled) {
            _capability_roles[code][sig] = last_roles | shifted;
        } else {
            _capability_roles[code][sig] = last_roles & BITNOT(shifted);
        }
    }
}
","keccak256":"0x854b2fe5dcb2ab5277a1a1c3a2f97367c4c00f8076f746a88cfbde1eb43b2f15"},"contracts/voyage/facets/DataProviderFacet.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {WadRayMath} from "../../shared/libraries/WadRayMath.sol";
import {IVToken} from "../interfaces/IVToken.sol";
import {AppStorage, ReserveData, ReserveConfigurationMap, Tranche, VaultConfig, LoanList, RepaymentData} from "../libraries/LibAppStorage.sol";
import {LibLiquidity} from "../libraries/LibLiquidity.sol";
import {LibLoan} from "../libraries/LibLoan.sol";
import {LibVault} from "../libraries/LibVault.sol";
import {LibReserveConfiguration} from "../libraries/LibReserveConfiguration.sol";

struct CreditLineData {
    uint256 totalDebt;
    LoanList loanList;
    uint256 totalMargin;
    uint256 withdrawableSecurityDeposit;
    uint256 creditLimit;
    uint256 spendableBalance;
    uint256 gav;
    uint256 ltv;
    uint256 healthFactor;
}

contract DataProviderFacet {
    using WadRayMath for uint256;
    using LibReserveConfiguration for ReserveConfigurationMap;

    AppStorage internal s;

    struct PoolData {
        uint256 totalLiquidity;
        uint256 juniorLiquidity;
        uint256 seniorLiquidity;
        uint256 juniorLiquidityRate;
        uint256 seniorLiquidityRate;
        uint256 totalDebt;
        uint256 utilizationRate;
        uint256 trancheRatio;
        uint256 decimals;
        string symbol;
        bool isActive;
    }

    struct UserPoolData {
        uint256 juniorTrancheBalance;
        uint256 withdrawableJuniorTrancheBalance;
        uint256 seniorTrancheBalance;
        uint256 withdrawableSeniorTrancheBalance;
        uint256 decimals;
    }

    struct PoolConfiguration {
        uint256 liquidationBonus;
        uint256 marginRequirement;
        uint256 minMargin;
        uint256 maxMargin;
        uint256 apr;
        uint256 loanInterval;
        uint256 loanTenure;
        uint256 incomeRatio;
        bool isInitialized;
        bool isActive;
    }

    struct FungibleTokenData {
        string symbol;
        address tokenAddress;
    }

    function getPoolConfiguration(address _reserve)
        external
        view
        returns (PoolConfiguration memory)
    {
        PoolConfiguration memory poolConfiguration;
        ReserveConfigurationMap memory config = LibReserveConfiguration
            .getConfiguration(_reserve);
        poolConfiguration.liquidationBonus = config.getLiquidationBonus();
        poolConfiguration.incomeRatio = config.getIncomeRatio();
        (
            poolConfiguration.marginRequirement,
            poolConfiguration.minMargin,
            poolConfiguration.maxMargin
        ) = config.getMarginParams();
        (poolConfiguration.isActive, , ) = config.getFlags();

        return poolConfiguration;
    }

    function getPoolData(address _asset)
        external
        view
        returns (PoolData memory)
    {
        LibLiquidity.DepositAndDebt memory depositAndDebt = LibLiquidity
            .getDepositAndDebt(_asset);
        IERC20Metadata token = IERC20Metadata(_asset);

        PoolData memory poolData;
        poolData.juniorLiquidity = depositAndDebt.juniorDepositAmount;
        poolData.seniorLiquidity = depositAndDebt.seniorDepositAmount;
        poolData.totalLiquidity =
            depositAndDebt.seniorDepositAmount +
            depositAndDebt.juniorDepositAmount;
        poolData.juniorLiquidityRate = LibLiquidity.getLiquidityRate(
            _asset,
            Tranche.JUNIOR
        );
        poolData.seniorLiquidityRate = LibLiquidity.getLiquidityRate(
            _asset,
            Tranche.SENIOR
        );
        poolData.totalDebt = depositAndDebt.totalDebt;
        if (depositAndDebt.seniorDepositAmount == 0) {
            poolData.trancheRatio = 0;
        } else {
            poolData.trancheRatio = depositAndDebt.juniorDepositAmount.rayDiv(
                depositAndDebt.seniorDepositAmount
            );
        }

        poolData.decimals = token.decimals();
        poolData.utilizationRate = LibLiquidity.utilizationRate(_asset);
        poolData.symbol = token.symbol();
        (poolData.isActive, , ) = LibReserveConfiguration
            .getConfiguration(_asset)
            .getFlags();

        return poolData;
    }

    function getDepositTokens(address _asset)
        public
        view
        returns (address senior, address junior)
    {
        ReserveData memory reserve = LibLiquidity.getReserveData(_asset);
        senior = reserve.seniorDepositTokenAddress;
        junior = reserve.juniorDepositTokenAddress;
    }

    function getVault(address _user) external view returns (address) {
        return LibVault.getVaultAddress(_user);
    }

    function getPoolTokens()
        external
        view
        returns (FungibleTokenData[] memory tokens)
    {
        address[] memory reserveList = LibLiquidity.getReserveList();

        FungibleTokenData[] memory reserves = new FungibleTokenData[](
            reserveList.length
        );

        for (uint256 i = 0; i < reserveList.length; ) {
            address reserveAddress = reserveList[i];
            reserves[i] = FungibleTokenData({
                symbol: IERC20Metadata(reserveAddress).symbol(),
                tokenAddress: reserveAddress
            });
            unchecked {
                ++i;
            }
        }

        return reserves;
    }

    function getUserPoolData(address _reserve, address _user)
        external
        view
        returns (UserPoolData memory)
    {
        UserPoolData memory userPoolData;
        IERC20Metadata token = IERC20Metadata(_reserve);

        uint256 seniorTrancheWithdrawable = LibLiquidity.balance(
            _reserve,
            _user,
            Tranche.SENIOR
        );
        uint256 seniorTrancheUnbonding = LibLiquidity.unbonding(
            _reserve,
            _user,
            Tranche.SENIOR
        );
        uint256 seniorTrancheTotalBalance = seniorTrancheWithdrawable +
            seniorTrancheUnbonding;
        uint256 juniorTrancheWithdrawable = LibLiquidity.balance(
            _reserve,
            _user,
            Tranche.JUNIOR
        );
        uint256 juniorTrancheUnbonding = LibLiquidity.unbonding(
            _reserve,
            _user,
            Tranche.JUNIOR
        );
        uint256 juniorTrancheTotalBalance = juniorTrancheWithdrawable +
            juniorTrancheUnbonding;

        userPoolData.juniorTrancheBalance = juniorTrancheTotalBalance;
        userPoolData
            .withdrawableJuniorTrancheBalance = juniorTrancheWithdrawable;
        userPoolData.seniorTrancheBalance = seniorTrancheTotalBalance;
        userPoolData
            .withdrawableSeniorTrancheBalance = seniorTrancheWithdrawable;
        userPoolData.decimals = token.decimals();

        return userPoolData;
    }

    function getCreditLineData(address _vault, address _reserve)
        external
        view
        returns (CreditLineData memory)
    {
        CreditLineData memory creditLineData;
        uint256 principal;
        uint256 interest;
        LoanList memory loanList;
        (loanList.head, loanList.tail) = LibLoan.getLoanList(_reserve, _vault);
        (principal, interest) = LibVault.getVaultDebt(_reserve, _vault);
        creditLineData.loanList = loanList;
        creditLineData.totalDebt = principal + interest;
        creditLineData.totalMargin = LibVault.getMargin(_vault, _reserve);
        creditLineData.withdrawableSecurityDeposit = LibVault
            .getTotalWithdrawableMargin(_vault, _reserve);
        creditLineData.creditLimit = LibVault.getCreditLimit(_vault, _reserve);
        creditLineData.spendableBalance = LibVault.getAvailableCredit(
            _vault,
            _reserve
        );
        creditLineData.ltv = creditLineData.totalDebt == 0
            ? 1
            : (creditLineData.gav + creditLineData.totalMargin).rayDiv(
                creditLineData.totalDebt
            );

        return creditLineData;
    }

    function getLoanDetail(
        address _vault,
        address _reserve,
        uint256 _loanId
    ) external view returns (LibLoan.LoanDetail memory) {
        return LibLoan.getLoanDetail(_reserve, _vault, _loanId);
    }

    function getRepayment(
        address _valut,
        address _reserve,
        uint256 _loanId
    ) external view returns (RepaymentData[] memory) {
        return LibLoan.getRepayment(_valut, _reserve, _loanId);
    }

    function pendingSeniorWithdrawals(address _user, address _reserve)
        public
        view
        returns (uint256[] memory, uint256[] memory)
    {
        ReserveData memory reserve = LibLiquidity.getReserveData(_reserve);

        (uint256[] memory times, uint256[] memory amounts) = IVToken(
            reserve.seniorDepositTokenAddress
        ).unbonding(_user);

        return (times, amounts);
    }

    function pendingJuniorWithdrawals(address _user, address _reserve)
        public
        view
        returns (uint256[] memory, uint256[] memory)
    {
        ReserveData memory reserve = LibLiquidity.getReserveData(_reserve);

        (uint256[] memory times, uint256[] memory amounts) = IVToken(
            reserve.juniorDepositTokenAddress
        ).unbonding(_user);

        return (times, amounts);
    }

    function getMarginConfiguration(address _asset)
        external
        view
        returns (
            uint256,
            uint256,
            uint256
        )
    {
        ReserveConfigurationMap memory conf = LibReserveConfiguration
            .getConfiguration(_asset);
        uint256 decimals = conf.getDecimals();
        uint256 assetUnit = 10**decimals;
        (
            uint256 min,
            uint256 max,
            uint256 marginRequirement
        ) = LibReserveConfiguration.getConfiguration(_asset).getMarginParams();
        return (min * assetUnit, max * assetUnit, marginRequirement);
    }
}
","keccak256":"0x4d95915f04e3c8b348c8f3251ad034bad7f40c3abaa1c6cf4b4884e5fb0b048d","license":"GPL-3.0"},"contracts/voyage/facets/DiamondVersionFacet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {Snapshot} from "../interfaces/IDiamondVersionFacet.sol";
import {AppStorage, Storage, LibAppStorage} from "../libraries/LibAppStorage.sol";
import {IDiamondLoupe} from "../../shared/diamond/interfaces/IDiamondLoupe.sol";
import {IDiamondCut} from "../../shared/diamond/interfaces/IDiamondCut.sol";
import {DiamondCutFacet} from "../../shared/diamond/facets/DiamondCutFacet.sol";

contract DiamondVersionFacet is Storage {
    // increments `version` and stores snapshot in _snapshotMap
    // these must be computed offchain, and called by protocol admin (including init args encoding)
    function registerUpgrade(
        address init,
        bytes memory initArgs,
        IDiamondLoupe.Facet[] memory facets
    ) public authorised {
        LibAppStorage.diamondStorage().currentVersion += 1;
        uint256 version = LibAppStorage.diamondStorage().currentVersion;
        LibAppStorage.diamondStorage().snapshotMap[version].init = init;
        LibAppStorage.diamondStorage().snapshotMap[version].initArgs = initArgs;
        for (uint256 i = 0; i < facets.length; ) {
            LibAppStorage.diamondStorage().snapshotMap[version].facets.push(
                facets[i]
            );
            unchecked {
                i++;
            }
        }
    }

    function getUpgrade(address _vault)
        public
        returns (IDiamondCut.FacetCut[] memory)
    {
        IDiamondCut.FacetCut[] storage facetCuts = s.upgradeParam.facetCuts[
            msg.sender
        ];
        Snapshot memory snapshot = s.snapshotMap[s.currentVersion];
        IDiamondLoupe loupe = IDiamondLoupe(_vault);
        IDiamondLoupe.Facet[] memory currentFacets = loupe.facets();

        mapping(bytes4 => address) storage existingSelectorFacetMap = s
            .upgradeParam
            .existingSelectorFacetMap[msg.sender];
        bytes4[] storage existingSelectors = s.upgradeParam.existingSelectors[
            msg.sender
        ];

        mapping(bytes4 => bool) storage newSelectorSet = s
            .upgradeParam
            .newSelectorSet[msg.sender];
        bytes4[] storage newSelectors = s.upgradeParam.newSelectors[msg.sender];

        for (uint256 i = 0; i < currentFacets.length; ) {
            IDiamondLoupe.Facet memory facet = currentFacets[i];
            for (uint256 j = 0; j < facet.functionSelectors.length; ) {
                bytes4 selector = facet.functionSelectors[j];
                newSelectors.push(selector);
                existingSelectorFacetMap[selector] = facet.facetAddress;
                unchecked {
                    ++j;
                }
            }
            unchecked {
                ++i;
            }
        }

        // at this point, we have to compute which facets to add/replace/delete
        // the logic is identical to the deployment script in voyager_001.ts
        // first, compute the selectors to add or replace
        for (uint256 i = 0; i < snapshot.facets.length; ) {
            IDiamondLoupe.Facet memory candidateFacet = snapshot.facets[i];
            for (uint256 j = 0; j < candidateFacet.functionSelectors.length; ) {
                bytes4 selector = candidateFacet.functionSelectors[j];
                // add it to newSelectorSet
                newSelectorSet[selector] = true;
                address currentHostFacetAddress = existingSelectorFacetMap[
                    selector
                ];
                if (currentHostFacetAddress != address(0)) {
                    if (
                        currentHostFacetAddress != candidateFacet.facetAddress
                    ) {
                        s.upgradeParam.selectorsReplaced[i].push(selector);
                    }
                } else {
                    s.upgradeParam.selectorsAdded[i].push(selector);
                }
                unchecked {
                    ++j;
                }
            }

            if (s.upgradeParam.selectorsAdded[i].length > 0) {
                IDiamondCut.FacetCut memory facetCut;
                facetCut.functionSelectors = s.upgradeParam.selectorsAdded[i];
                facetCut.facetAddress = candidateFacet.facetAddress;
                facetCut.action = IDiamondCut.FacetCutAction.Add;

                facetCuts[s.upgradeParam.facetCutSize[msg.sender]] = facetCut;
                s.upgradeParam.facetCutSize[msg.sender]++;

                // clean storage right away
                delete s.upgradeParam.selectorsAdded[i];
            }

            if (s.upgradeParam.selectorsReplaced[i].length > 0) {
                IDiamondCut.FacetCut memory facetCut;
                facetCut.functionSelectors = s.upgradeParam.selectorsReplaced[
                    i
                ];
                facetCut.facetAddress = candidateFacet.facetAddress;
                facetCut.action = IDiamondCut.FacetCutAction.Replace;

                facetCuts[s.upgradeParam.facetCutSize[msg.sender]] = facetCut;
                s.upgradeParam.facetCutSize[msg.sender]++;

                // clean storage right away
                delete s.upgradeParam.selectorsReplaced[i];
            }
            unchecked {
                ++i;
            }
        }

        // now just get the XOR of existing and new selectors to find the removed set
        for (uint256 i = 0; i < existingSelectors.length; ) {
            if (!newSelectorSet[existingSelectors[i]]) {
                s.upgradeParam.selectorsRemoved[i].push(existingSelectors[i]);
            }

            if (s.upgradeParam.selectorsRemoved[i].length > 0) {
                IDiamondCut.FacetCut memory facetCut;
                facetCut.functionSelectors = s.upgradeParam.selectorsRemoved[i];
                facetCut.facetAddress = address(0);
                facetCut.action = IDiamondCut.FacetCutAction.Remove;

                facetCuts[s.upgradeParam.facetCutSize[msg.sender]] = facetCut;
                s.upgradeParam.facetCutSize[msg.sender]++;

                // clean storage right away
                delete s.upgradeParam.selectorsRemoved[i];
            }

            unchecked {
                ++i;
            }
        }

        IDiamondCut.FacetCut[] memory ret = new IDiamondCut.FacetCut[](
            s.upgradeParam.facetCutSize[msg.sender]
        );
        for (uint256 i = 0; i < s.upgradeParam.facetCutSize[msg.sender]; ) {
            ret[i] = facetCuts[i];
            unchecked {
                ++i;
            }
        }

        LibAppStorage.cleanUpgradeParam();

        return ret;
    }

    function currentVersion() public view returns (uint256, bytes32) {
        uint256 version = LibAppStorage.diamondStorage().currentVersion;
        Snapshot memory snapshot = LibAppStorage.diamondStorage().snapshotMap[
            version
        ];
        return (version, computeSnapshotChecksum(snapshot));
    }

    function isUpToDate(uint256 _version) public view returns (bool) {
        return _version == LibAppStorage.diamondStorage().currentVersion;
    }
}
","keccak256":"0x0b698d4398ee534118e46cec71e4bf43045a79f9a3d8ab57eca70668446aa503","license":"MIT"},"contracts/voyage/facets/LoanFacet.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ERC4626} from "@rari-capital/solmate/src/mixins/ERC4626.sol";
import {LibLiquidity} from "../libraries/LibLiquidity.sol";
import {LibLoan} from "../libraries/LibLoan.sol";
import {LibVault} from "../libraries/LibVault.sol";
import {IReserveInterestRateStrategy} from "../interfaces/IReserveInterestRateStrategy.sol";
import {IVToken} from "../interfaces/IVToken.sol";
import {IPriceOracle} from "../interfaces/IPriceOracle.sol";
import {LibAppStorage, AppStorage, Storage, BorrowData, BorrowState, Loan, ReserveConfigurationMap, ReserveData} from "../libraries/LibAppStorage.sol";
import {LibReserveConfiguration} from "../libraries/LibReserveConfiguration.sol";
import {WadRayMath} from "../../shared/libraries/WadRayMath.sol";
import {PercentageMath} from "../../shared/libraries/PercentageMath.sol";
import {VaultDataFacet} from "../../vault/facets/VaultDataFacet.sol";
import {VaultMarginFacet} from "../../vault/facets/VaultMarginFacet.sol";
import {VaultAssetFacet} from "../../vault/facets/VaultAssetFacet.sol";

contract LoanFacet is Storage {
    using WadRayMath for uint256;
    using SafeERC20 for IERC20;
    using PercentageMath for uint256;
    using LibReserveConfiguration for ReserveConfigurationMap;

    uint256 public immutable TEN_THOUSANDS = 10000;

    struct ExecuteBorrowParams {
        address asset;
        address user;
        uint256 amount;
        uint256 term;
        uint256 epoch;
        uint256 liquidityRate;
        uint256 borrowRate;
    }

    struct ExecuteLiquidateParams {
        address reserve;
        address vault;
        uint256 loanId;
        uint256 repaymentId;
        uint256 principal;
        uint256 interest;
        uint256 totalDebt;
        uint256 totalFromMargin;
        uint256 totalToLiquidate;
        uint256 discount;
        uint256 totalSlash;
        uint256 amountNeedExtra;
        uint256 juniorTrancheAmount;
        uint256 receivedAmount;
        address liquidator;
        uint256 floorPrice;
        uint256 totalNFTNums;
        uint256 numNFTsToLiquidate;
        uint256 gracePeriod;
        uint256 liquidationBonus;
        uint256 marginRequirement;
        uint256 slashedMargin;
        uint256 writeDownAmount;
        uint256 totalAssetFromJuniorTranche;
        bool isFinal;
    }

    struct ExecuteRepayParams {
        uint256 principal;
        uint256 interest;
        uint256 total;
        uint256 totalDebt;
    }

    event Borrow(
        address indexed _vault,
        address indexed _asset,
        uint256 _loanId,
        uint256 _principal,
        uint256 _interest,
        uint256 _apr
    );

    event Repayment(
        address indexed _user,
        address indexed _vault,
        address indexed _asset,
        uint256 _loanId,
        uint256 _repaymentId,
        uint256 _amount,
        bool isFinal
    );

    event Liquidate(
        address indexed _liquidator,
        address indexed _vault,
        address indexed _asset,
        uint256 _drowDownId,
        uint256 _repaymentId,
        uint256 _debt,
        uint256 _margin,
        uint256 _collateral,
        uint256 _fromJuniorTranche,
        uint256 _amountToWriteDown
    );

    event CollateralTransferred(address from, address to, uint256[] tokenIds);

    function borrow(
        address _asset,
        uint256 _amount,
        address payable _vault
    ) external whenNotPaused {
        // 0. check if the user owns the vault
        if (LibVault.getVaultAddress(_msgSender()) != _vault) {
            revert Unauthorised();
        }

        // 1. check if pool liquidity is sufficient
        ReserveData memory reserveData = LibLiquidity.getReserveData(_asset);

        uint256 availableSeniorLiquidity = IERC20(_asset).balanceOf(
            reserveData.seniorDepositTokenAddress
        );
        if (availableSeniorLiquidity < _amount) {
            revert InsufficientLiquidity();
        }

        // 3. check credit limit
        uint256 availableCreditLimit = LibVault.getAvailableCredit(
            _vault,
            _asset
        );

        if (availableCreditLimit < _amount) {
            revert InsufficientCreditLimit();
        }

        BorrowState memory borrowState = LibLoan.getBorrowState(_asset);

        ExecuteBorrowParams memory executeBorrowParams = previewBorrowParams(
            _asset,
            _amount
        );

        LibLoan.updateStateOnBorrow(
            _asset,
            _amount,
            borrowState.totalDebt + borrowState.totalInterest,
            executeBorrowParams.borrowRate
        );

        (uint256 loanId, Loan memory loan) = LibLoan.insertDebt(
            _asset,
            _vault,
            _amount,
            executeBorrowParams.term,
            executeBorrowParams.epoch,
            executeBorrowParams.borrowRate
        );

        IVToken(reserveData.seniorDepositTokenAddress).transferUnderlyingTo(
            VaultDataFacet(_vault).creditEscrow(_asset),
            _amount
        );

        emit Borrow(
            _vault,
            _asset,
            loanId,
            loan.principal,
            loan.interest,
            loan.apr
        );
    }

    function repay(
        address _asset,
        uint256 _loan,
        address payable _vault
    ) external whenNotPaused {
        ExecuteRepayParams memory params;

        // 0. check if the user owns the vault
        if (LibVault.getVaultAddress(_msgSender()) != _vault) {
            revert Unauthorised();
        }

        // 1. check draw down to get principal and interest
        (params.principal, params.interest) = LibLoan.getPMT(
            _asset,
            _vault,
            _loan
        );
        if (params.principal + params.interest == 0) {
            revert InvalidDebt();
        }

        params.total = params.principal + params.interest;

        // 2. update liquidity index and interest rate
        BorrowState memory borrowState = LibLoan.getBorrowState(_asset);
        uint256 totalDebt = borrowState.totalDebt + borrowState.totalInterest;
        uint256 avgBorrowRate = borrowState.avgBorrowRate;
        LibLoan.updateStateOnRepayment(
            _asset,
            params.principal + params.interest,
            params.totalDebt,
            avgBorrowRate
        );

        // 3. update repay data
        (uint256 repaymentId, bool isFinal) = LibLoan.repay(
            _asset,
            _vault,
            _loan,
            params.principal,
            params.interest,
            false
        );

        // 4. transfer underlying asset
        ReserveData memory reserveData = LibLiquidity.getReserveData(_asset);
        uint256 incomeRatio = LibReserveConfiguration
            .getConfiguration(_asset)
            .getIncomeRatio();
        uint256 seniorInterest = params.interest.percentMul(incomeRatio);

        IERC20(_asset).safeTransferFrom(
            _msgSender(),
            reserveData.seniorDepositTokenAddress,
            params.principal + seniorInterest
        );

        IERC20(_asset).safeTransferFrom(
            _msgSender(),
            reserveData.juniorDepositTokenAddress,
            params.interest - seniorInterest
        );

        emit Repayment(
            _msgSender(),
            _vault,
            _asset,
            _loan,
            repaymentId,
            params.total,
            isFinal
        );
    }

    function liquidate(
        address _reserve,
        address _vault,
        uint256 _loanId
    ) external whenNotPaused {
        ExecuteLiquidateParams memory param;
        param.reserve = _reserve;
        ReserveData memory reserveData = LibLiquidity.getReserveData(
            param.reserve
        );
        ReserveConfigurationMap memory reserveConf = LibReserveConfiguration
            .getConfiguration(_reserve);

        // 1. prepare basic info and some strategy parameters
        param.reserve = _reserve;
        param.vault = _vault;
        param.loanId = _loanId;
        param.liquidator = _msgSender();
        (
            param.liquidationBonus,
            param.marginRequirement,
            param.gracePeriod
        ) = reserveConf.getLiquidationParams();
        param.totalNFTNums = VaultDataFacet(param.vault).getTotalNFTNumbers(
            reserveData.nftAddress
        );

        LibLoan.LoanDetail memory loanDetail = LibLoan.getLoanDetail(
            param.reserve,
            param.vault,
            param.loanId
        );

        // 2. check if the debt is qualified to be liquidated
        if (
            block.timestamp <= loanDetail.nextPaymentDue ||
            block.timestamp - loanDetail.nextPaymentDue <=
            param.gracePeriod * LibLoan.SECOND_PER_DAY
        ) {
            revert InvalidLiquidate();
        }

        // 3.1 if it is, get debt info
        (param.principal, param.interest) = LibLoan.getPMT(
            param.reserve,
            param.vault,
            param.loanId
        );
        param.totalDebt = param.principal + param.interest;
        param.totalFromMargin = param
            .totalDebt
            .wadToRay()
            .rayMul(param.marginRequirement)
            .rayToWad();
        param.totalToLiquidate = param.totalDebt - param.totalFromMargin;
        param.discount = getDiscount(
            param.totalToLiquidate,
            param.liquidationBonus
        );

        // 3.2 get floor price from oracle contract
        IPriceOracle priceOracle = IPriceOracle(reserveData.priceOracle);
        param.floorPrice = priceOracle.getTwap(reserveData.nftAddress);

        if (param.floorPrice == 0) {
            revert InvalidFloorPrice();
        }
        param.numNFTsToLiquidate =
            (param.totalToLiquidate - param.discount) /
            param.floorPrice;
        param.totalSlash = param.totalFromMargin + param.discount;

        // 4.1 slash margin account
        param.slashedMargin = VaultMarginFacet(param.vault).slash(
            param.reserve,
            payable(address(this)),
            param.totalSlash
        );
        param.receivedAmount = param.receivedAmount + param.slashedMargin;

        param.amountNeedExtra = param.totalSlash - param.slashedMargin;

        // 4.2 transfer from liquidator
        IERC20(param.reserve).safeTransferFrom(
            param.liquidator,
            address(this),
            param.totalToLiquidate
        );
        param.receivedAmount = param.receivedAmount + param.totalToLiquidate;

        if (param.totalNFTNums < param.numNFTsToLiquidate) {
            uint256 missingNFTNums = param.numNFTsToLiquidate -
                param.totalNFTNums;
            param.amountNeedExtra =
                missingNFTNums *
                param.floorPrice +
                param.amountNeedExtra;
            param.numNFTsToLiquidate = param.totalNFTNums;
        }

        // 4.3 sell nft
        uint256[] memory ids = VaultAssetFacet(param.vault).transferNFT(
            reserveData.nftAddress,
            param.liquidator,
            param.numNFTsToLiquidate
        );
        emit CollateralTransferred(param.vault, param.liquidator, ids);

        // 4.4 transfer from junior tranche
        param.totalAssetFromJuniorTranche = ERC4626(
            reserveData.juniorDepositTokenAddress
        ).totalAssets();

        if (param.totalAssetFromJuniorTranche >= param.amountNeedExtra) {
            IVToken(reserveData.juniorDepositTokenAddress).transferUnderlyingTo(
                    address(this),
                    param.amountNeedExtra
                );
            param.receivedAmount = param.receivedAmount + param.amountNeedExtra;
            param.juniorTrancheAmount = param.amountNeedExtra;
        } else {
            IVToken(reserveData.juniorDepositTokenAddress).transferUnderlyingTo(
                    address(this),
                    param.totalAssetFromJuniorTranche
                );
            param.juniorTrancheAmount = param.totalAssetFromJuniorTranche;

            param.writeDownAmount =
                param.amountNeedExtra -
                param.totalAssetFromJuniorTranche;
            param.receivedAmount =
                param.receivedAmount +
                param.totalAssetFromJuniorTranche;
        }

        (param.repaymentId, param.isFinal) = LibLoan.repay(
            param.reserve,
            param.vault,
            param.loanId,
            param.principal,
            param.interest,
            true
        );

        emit Repayment(
            _msgSender(),
            param.vault,
            param.reserve,
            param.loanId,
            param.repaymentId,
            param.totalDebt,
            param.isFinal
        );

        IERC20(param.reserve).safeTransfer(
            reserveData.seniorDepositTokenAddress,
            param.receivedAmount
        );

        emit Liquidate(
            _msgSender(),
            _vault,
            _reserve,
            param.loanId,
            param.repaymentId,
            param.totalDebt,
            param.slashedMargin,
            param.totalToLiquidate,
            param.juniorTrancheAmount,
            param.writeDownAmount
        );
    }

    function getVaultDebt(address _reserve, address _vault)
        public
        view
        returns (uint256, uint256)
    {
        return LibVault.getVaultDebt(_reserve, _vault);
    }

    function previewBorrowParams(address _asset, uint256 _amount)
        public
        view
        returns (ExecuteBorrowParams memory)
    {
        ExecuteBorrowParams memory executeBorrowParams;
        ReserveData memory reserveData = LibLiquidity.getReserveData(_asset);
        ReserveConfigurationMap memory reserveConf = LibReserveConfiguration
            .getConfiguration(_asset);

        // 4. update debt logic
        (executeBorrowParams.epoch, executeBorrowParams.term) = reserveConf
            .getBorrowParams();

        // 5. update liquidity index and interest rate
        BorrowState memory borrowState = LibLoan.getBorrowState(_asset);

        (
            executeBorrowParams.liquidityRate,
            executeBorrowParams.borrowRate
        ) = IReserveInterestRateStrategy(
            reserveData.interestRateStrategyAddress
        ).calculateInterestRates(
                _asset,
                reserveData.seniorDepositTokenAddress,
                0,
                _amount,
                borrowState.totalDebt,
                borrowState.avgBorrowRate
            );

        return executeBorrowParams;
    }

    function getTotalPaidAndRedeemed(address _reserve, address _vault)
        public
        view
        returns (uint256, uint256)
    {
        return LibVault.getTotalPaidAndRedeemed(_reserve, _vault);
    }

    function increaseTotalRedeemed(
        address _reserve,
        address _vault,
        uint256 _amount
    ) external {
        require(msg.sender == address(this));
        return LibVault.increaseTotalRedeemed(_reserve, _vault, _amount);
    }

    /// @notice Returns the total outstanding principal debt for a particular underlying asset pool
    /// @param underlyingAsset the address of the underlying reserve asset
    /// @return The total outstanding principal owed to depositors.
    function principalBalance(address underlyingAsset)
        external
        view
        returns (uint256)
    {
        BorrowState memory borrowState = LibLoan.getBorrowState(
            underlyingAsset
        );
        return borrowState.totalDebt;
    }

    /// @notice Returns the total outstanding interest debt for a particular underlying asset pool
    /// @param underlyingAsset the address of the underlying reserve asset
    /// @return The total outstanding interest owed to depositors.
    function interestBalance(address underlyingAsset)
        external
        view
        returns (uint256)
    {
        BorrowState memory borrowState = LibLoan.getBorrowState(
            underlyingAsset
        );
        return borrowState.totalInterest;
    }

    function getDiscount(uint256 _value, uint256 _liquidationBonus)
        private
        pure
        returns (uint256)
    {
        uint256 withBonus = _value.percentMul(_liquidationBonus);
        return withBonus - _value;
    }
}

/* --------------------------------- errors -------------------------------- */
error Unauthorised();
error InsufficientLiquidity();
error InsufficientCreditLimit();
error InvalidDebt();
error InvalidLiquidate();
error InvalidFloorPrice();
