pragma solidity ^0.8.9;

import {LibAppStorage, AppStorage, Authorisation} from "./LibAppStorage.sol";

library LibSecurity {
    function isAuthorised(
        Authorisation storage auth,
        address src,
        address dst,
        bytes4 selector
    ) internal view returns (bool) {
        return
            auth.rbac.canCall(src, dst, selector) ||
            auth.acl.canCall(src, dst, selector);
    }

    function isAuthorisedInbound(
        Authorisation storage auth,
        address src,
        bytes4 selector
    ) internal view returns (bool) {
        // s.security.acl/rbac is an instance ds-roles and implements DSAuthority interface
        return
            auth.rbac.canCall(src, address(this), selector) ||
            auth.acl.canCall(src, address(this), selector);
    }

    function isAuthorisedOutbound(
        Authorisation storage auth,
        address dst,
        bytes4 selector
    ) internal view returns (bool) {
        // s.security.acl/rbac is an instance ds-guard and implements DSAuthority interface
        return
            auth.rbac.canCall(msg.sender, dst, selector) ||
            auth.acl.canCall(msg.sender, dst, selector);
    }

    // role can be a enum, but cast to uint8 before calling grantRole
    function grantRole(
        Authorisation storage auth,
        address user,
        uint8 role,
        bool enabled
    ) internal {
        // grant role
        auth.rbac.setUserRole(user, role, enabled);
    }

    function grantRolePermission(
        Authorisation storage auth,
        uint8 role,
        address target,
        bytes4 sig
    ) internal {
        // give a role a permission
        auth.rbac.setRoleCapability(role, target, sig, true);
    }

    function revokeRolePermission(
        Authorisation storage auth,
        uint8 role,
        address target,
        bytes4 sig
    ) internal {
        // revoke a role permission
        auth.rbac.setRoleCapability(role, target, sig, false);
    }

    function grantPermissions(
        Authorisation storage auth,
        address src,
        address dst,
        bytes4[] memory sig
    ) internal {
        for (uint256 i = 0; i < sig.length; i++) {
            grantPermission(auth, src, dst, sig[i]);
        }
    }

    function grantPermission(
        Authorisation storage auth,
        address src,
        address dst,
        bytes4 sig
    ) internal {
        // allow src to call dst.sig
        auth.acl.permit(src, dst, sig);
    }

    function revokePermission(
        Authorisation storage auth,
        address src,
        address dst,
        bytes4 sig
    ) internal {
        auth.acl.forbid(src, dst, sig);
    }
}
","keccak256":"0x4ca4a6b133cc63ed12705db3fb8c6d166fc394ca9be53743c6857502077fd029","license":"GPL-3.0"},"contracts/voyage/libraries/LibValidation.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {LibReserveConfiguration} from "./LibReserveConfiguration.sol";
import {ReserveConfigurationMap, ReserveData} from "./LibAppStorage.sol";

library ValidationLogic {
    using LibReserveConfiguration for ReserveConfigurationMap;

    /**
     * @dev Validates a deposit token
     * @param reserve The reserve object on which the user is depositing
     * @param amount The amount to be deposited
     **/
    function validateDeposit(ReserveData storage reserve, uint256 amount)
        internal
        view
    {
        (bool isActive, bool isFrozen, ) = reserve.configuration.getFlags();
        if (amount == 0) {
            revert InvalidAmount();
        }
        if (!isActive) {
            revert ReserveNotActive();
        }
        if (isFrozen) {
            revert ReserveFrozen();
        }
    }
}

/* --------------------------------- errors -------------------------------- */
error InvalidAmount();
error ReserveNotActive();
error ReserveFrozen();
","keccak256":"0xdf3c60f106c5da465e9388069dfac637f10189618b3285c27708d77166f26242","license":"GPL-3.0"},"contracts/voyage/libraries/LibVault.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {BeaconProxy} from "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import {UpgradeableBeacon} from "@openzeppelin/contracts/proxy/beacon/UpgradeableBeacon.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {LibAppStorage, AppStorage, BorrowData, VaultConfig, NFTInfo, DiamondFacet, ReserveConfigurationMap} from "./LibAppStorage.sol";
import {LibReserveConfiguration} from "./LibReserveConfiguration.sol";
import {IExternalAdapter} from "../interfaces/IExternalAdapter.sol";
import {WadRayMath} from "../../shared/libraries/WadRayMath.sol";
import {PercentageMath} from "../../shared/libraries/PercentageMath.sol";
import {VaultDataFacet} from "../../vault/facets/VaultDataFacet.sol";
import {VaultAssetFacet} from "../../vault/facets/VaultAssetFacet.sol";

library LibVault {
    using WadRayMath for uint256;
    using PercentageMath for uint256;
    using LibReserveConfiguration for ReserveConfigurationMap;

    function recordVault(address _owner, address _vault)
        internal
        returns (uint256)
    {
        AppStorage storage s = LibAppStorage.diamondStorage();
        require(s.vaultMap[_owner] == address(0), "one vault per owner");
        s.vaults.push(_vault);
        LibAppStorage.diamondStorage().vaultMap[_owner] = _vault;
        return (s.vaults.length);
    }

    function initCreditLine(address _vault, address _asset)
        internal
        returns (address, address)
    {
        (address me, address ce) = VaultAssetFacet(_vault).initCreditLine(
            _asset
        );
        return (me, ce);
    }

    function setVaultBeacon(address _impl) internal {
        AppStorage storage s = LibAppStorage.diamondStorage();
        s.vaultBeacon = new UpgradeableBeacon(_impl);
    }

    function setVaultConfig(
        address _reserve,
        address _vault,
        uint256 _min,
        uint256 _max,
        uint256 _marginRequirement
    ) internal {
        AppStorage storage s = LibAppStorage.diamondStorage();
        VaultConfig memory config = VaultConfig({
            minMargin: _min,
            maxMargin: _max,
            marginRequirement: _marginRequirement,
            overrideGlobal: true
        });
        s.vaultConfigMap[_reserve][_vault] = config;
    }

    function updateNFTPrice(
        address _erc721Addr,
        uint256 _cardId,
        uint256 _cardPrice
    ) internal {
        AppStorage storage s = LibAppStorage.diamondStorage();
        s.nftInfo[_erc721Addr][_cardId].price = _cardPrice;
        s.nftInfo[_erc721Addr][_cardId].timestamp = block.timestamp;
    }

    function setNFTInfo(
        address _nft721,
        address _erc20,
        address _marketPlace
    ) internal {
        AppStorage storage s = LibAppStorage.diamondStorage();
        s.marketPlaceToAsset[_marketPlace] = _nft721;
        s.erc721AssetInfo[_nft721].marketplace = _marketPlace;
        s.erc721AssetInfo[_nft721].erc20Addr = _erc20;
    }

    /* ----------------------------- view functions ----------------------------- */
    function vaultBeacon() internal view returns (address) {
        AppStorage storage s = LibAppStorage.diamondStorage();
        return address(s.vaultBeacon);
    }

    function marginEscrowBeacon() internal view returns (address) {
        AppStorage storage s = LibAppStorage.diamondStorage();
        return address(s.marginEscrowBeacon);
    }

    function creditEscrowBeacon() internal view returns (address) {
        AppStorage storage s = LibAppStorage.diamondStorage();
        return address(s.creditEscrowBeacon);
    }

    function subVaultBeacon() internal view returns (address) {
        AppStorage storage s = LibAppStorage.diamondStorage();
        return address(s.subVaultBeacon);
    }

    function getVaultAddress(address _owner) internal view returns (address) {
        return LibAppStorage.diamondStorage().vaultMap[_owner];
    }

    function getVaultEscrowAddress(address _owner, address _asset)
        internal
        view
        returns (address, address)
    {
        address creditEscrow = VaultDataFacet(getVaultAddress(_owner))
            .creditEscrow(_asset);
        address marginEscrow = VaultDataFacet(getVaultAddress(_owner))
            .marginEscrow(_asset);
        return (creditEscrow, marginEscrow);
    }

    function getVaultDebt(address _reserve, address _vault)
        internal
        view
        returns (uint256, uint256)
    {
        AppStorage storage s = LibAppStorage.diamondStorage();
        BorrowData storage borrowData = s._borrowData[_reserve][_vault];
        return (borrowData.totalPrincipal, borrowData.totalInterest);
    }

    function getDiamondFacets() internal view returns (DiamondFacet memory) {
        AppStorage storage s = LibAppStorage.diamondStorage();
        return s.diamondFacet;
    }

    function getTotalPaidAndRedeemed(address _reserve, address _vault)
        internal
        view
        returns (uint256, uint256)
    {
        AppStorage storage s = LibAppStorage.diamondStorage();
        BorrowData storage borrowData = s._borrowData[_reserve][_vault];
        return (borrowData.totalPaid, borrowData.totalRedeemed);
    }

    function increaseTotalRedeemed(
        address _reserve,
        address _vault,
        uint256 _amount
    ) internal {
        AppStorage storage s = LibAppStorage.diamondStorage();
        BorrowData storage borrowData = s._borrowData[_reserve][_vault];
        borrowData.totalRedeemed = borrowData.totalRedeemed + _amount;
    }

    function getVaultConfig(address _reserve, address _vault)
        internal
        view
        returns (VaultConfig memory)
    {
        AppStorage storage s = LibAppStorage.diamondStorage();
        ReserveConfigurationMap memory conf = LibReserveConfiguration
            .getConfiguration(_reserve);
        uint256 decimals = conf.getDecimals();
        uint256 assetUnit = 10**decimals;
        VaultConfig memory vaultConfig = s.vaultConfigMap[_reserve][_vault];
        if (!vaultConfig.overrideGlobal) {
            (
                vaultConfig.minMargin,
                vaultConfig.maxMargin,
                vaultConfig.marginRequirement
            ) = LibReserveConfiguration
                .getConfiguration(_reserve)
                .getMarginParams();
        }
        vaultConfig.minMargin = vaultConfig.minMargin * assetUnit;
        vaultConfig.maxMargin = vaultConfig.maxMargin * assetUnit;

        return vaultConfig;
    }

    function getTokenAddrByMarketPlace(address _marketplace)
        internal
        view
        returns (address)
    {
        AppStorage storage s = LibAppStorage.diamondStorage();
        return s.marketPlaceToAsset[_marketplace];
    }

    function getMarketPlaceByAsset(address _asset)
        internal
        view
        returns (address)
    {
        AppStorage storage s = LibAppStorage.diamondStorage();
        return s.erc721AssetInfo[_asset].marketplace;
    }

    function getERC20ByAsset(address _asset) internal view returns (address) {
        AppStorage storage s = LibAppStorage.diamondStorage();
        return s.erc721AssetInfo[_asset].erc20Addr;
    }

    function getNFTInfo(address _erc721Addr, uint256 _tokenId)
        internal
        view
        returns (NFTInfo memory)
    {
        AppStorage storage s = LibAppStorage.diamondStorage();
        return s.nftInfo[_erc721Addr][_tokenId];
    }

    /**
     * @dev Get available credit
     * @param _vault user address
     * @param _reserve reserve address
     **/
    function getAvailableCredit(address _vault, address _reserve)
        internal
        view
        returns (uint256)
    {
        uint256 creditLimit = getCreditLimit(_vault, _reserve);
        (uint256 principal, uint256 interest) = getVaultDebt(_reserve, _vault);
        uint256 accumulatedDebt = principal + interest;
        if (creditLimit < accumulatedDebt) {
            return 0;
        }

        return creditLimit - accumulatedDebt;
    }

    /**
     * @dev Get credit limit for a specific reserve
     * @param _vault vault address
     * @return _reserve reserve address
     **/
    function getCreditLimit(address _vault, address _reserve)
        internal
        view
        returns (uint256)
    {
        uint256 currentMargin = getMargin(_vault, _reserve);
        VaultConfig memory vc = getVaultConfig(_reserve, _vault);
        require(vc.marginRequirement != 0, "margin requirement cannot be 0");
        return currentMargin.percentDiv(vc.marginRequirement);
    }

    function getMargin(address _vault, address _reserve)
        internal
        view
        returns (uint256)
    {
        return VaultDataFacet(_vault).getCurrentMargin(_reserve);
    }

    function getWithdrawableMargin(
        address _vault,
        address _reserve,
        address _user
    ) internal view returns (uint256) {
        return VaultDataFacet(_vault).withdrawableMargin(_reserve, _user);
    }

    function getTotalWithdrawableMargin(address _vault, address _reserve)
        internal
        view
        returns (uint256)
    {
        return VaultDataFacet(_vault).totalWithdrawableMargin(_reserve);
    }
}
","keccak256":"0x90c15acd6e1ca694ac2a9955ce3176756795748879047661571f6144fdd9e734","license":"GPL-3.0"},"contracts/voyage/tokenization/VToken.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {Context} from "@openzeppelin/contracts/utils/Context.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ERC4626, IERC4626} from "../../shared/tokenization/ERC4626.sol";
import {IVToken} from "../interfaces/IVToken.sol";

abstract contract VToken is Initializable, ERC4626, IVToken {
    using SafeERC20 for IERC20Metadata;

    address internal voyage;
    // user address => timestamp => amount
    mapping(address => mapping(uint256 => uint256)) private withdrawals;

    // user address => timestamp array
    mapping(address => uint256[]) private pendingTimestamp;

    uint256 public totalUnbonding;

    uint256 public cooldown = 7 days;

    event Claim(address receiver, uint256 amount);

    modifier onlyAdmin() {
        require(_msgSender() == voyage, "Not admin");
        _;
    }

    function initialize(address _voyage, address _asset) public initializer {
        IERC20Metadata underlying = IERC20Metadata(_asset);
        voyage = _voyage;
        __ERC20_init(underlying.name(), underlying.symbol());
        __ERC20Permit_init(underlying.name());
        __ERC4626_init(underlying);
    }

    function withdraw(
        uint256 _amount,
        address _receiver,
        address _owner
    ) public override(ERC4626, IERC4626) returns (uint256 shares) {
        shares = previewWithdraw(_amount); // No need to check for rounding error, previewWithdraw rounds up.
        if (msg.sender != _owner) {
            _spendAllowance(_owner, msg.sender, shares);
        }

        beforeWithdraw(_amount, shares);

        _burn(_owner, shares);
        pushWithdraw(_owner, _amount);

        emit Withdraw(msg.sender, _receiver, _owner, _amount, shares);
    }

    function claim(uint256 _index) public {
        uint256 amount = popWithdraw(msg.sender, _index);
        if (asset.balanceOf(address(this)) < amount) {
            revert InsufficientLiquidity();
        }
        asset.safeTransfer(msg.sender, amount);
    }

    function transferUnderlyingTo(address _target, uint256 _amount)
        public
        onlyAdmin
    {
        asset.safeTransfer(_target, _amount);
    }

    function pushWithdraw(address _user, uint256 _amount) internal {
        if (withdrawals[_user][block.timestamp] != 0) {
            revert InvalidWithdrawal();
        }
        withdrawals[_user][block.timestamp] = _amount;
        pendingTimestamp[_user].push(block.timestamp);
        totalUnbonding += _amount;
    }

    function popWithdraw(address _user, uint256 _index)
        internal
        returns (uint256)
    {
        uint256[] storage times = pendingTimestamp[_user];
        if (_index >= times.length) {
            revert InvalidIndex();
        }
        uint256 ts = times[_index];
        if (block.timestamp - ts <= cooldown) {
            revert CollDownError();
        }

        uint256 last = times[times.length - 1];
        times[_index] = last;
        times.pop();

        uint256 withdrawable = withdrawals[_user][ts];
        delete withdrawals[_user][ts];
        totalUnbonding -= withdrawable;
        return withdrawable;
    }

    function unbonding(address _user)
        public
        view
        returns (uint256[] memory, uint256[] memory)
    {
        uint256[] memory times = pendingTimestamp[_user];
        uint256[] memory amounts = new uint256[](times.length);

        for (uint256 i = 0; i < times.length; i++) {
            amounts[i] = withdrawals[_user][times[i]];
        }

        return (times, amounts);
    }
}

/* --------------------------------- errors -------------------------------- */
error InsufficientLiquidity();
error InvalidWithdrawal();
error InvalidIndex();
error CollDownError();
","keccak256":"0xf08f5c5ca88da2a821726cd1afa64900b5b7c568f6d7de002221983de068988b","license":"GPL-3.0"},"hardhat/console.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >= 0.4.22 <0.9.0;

library console {
	address constant CONSOLE_ADDRESS = address(0x000000000000000000636F6e736F6c652e6c6f67);

	function _sendLogPayload(bytes memory payload) private view {
		uint256 payloadLength = payload.length;
		address consoleAddress = CONSOLE_ADDRESS;
		assembly {
			let payloadStart := add(payload, 32)
			let r := staticcall(gas(), consoleAddress, payloadStart, payloadLength, 0, 0)
		}
	}

	function log() internal view {
		_sendLogPayload(abi.encodeWithSignature("log()"));
	}

	function logInt(int p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(int)", p0));
	}

	function logUint(uint p0) internal view {
		_sendLogPayload(abi.encodeWithSignature("log(uint)", p0));
	}

	function logString(string memory p0) internal view {
