contracts/voyage/facets/VaultFacet.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {UpgradeableBeacon} from "@openzeppelin/contracts/proxy/beacon/UpgradeableBeacon.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {BeaconProxy} from "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import {LibAppStorage, AppStorage, Storage, VaultConfig, NFTInfo, DiamondFacet, ReserveConfigurationMap} from "../libraries/LibAppStorage.sol";
import {LibVault} from "../libraries/LibVault.sol";
import {LibSecurity} from "../libraries/LibSecurity.sol";
import {LibReserveConfiguration} from "../libraries/LibReserveConfiguration.sol";
import {IVault} from "../../vault/Vault.sol";
import {IDiamondVersionFacet, Snapshot} from "../interfaces/IDiamondVersionFacet.sol";
import {IDiamondCut} from "../../shared/diamond/interfaces/IDiamondCut.sol";
import {DiamondCutFacet} from "../../shared/diamond/facets/DiamondCutFacet.sol";
import {DiamondVersionFacet} from "./DiamondVersionFacet.sol";

contract VaultFacet is Storage, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using LibReserveConfiguration for ReserveConfigurationMap;
    /* --------------------------------- events --------------------------------- */
    event VaultCreated(address _vault, address _owner, uint256 _numVaults);
    event VaultMarginCredited(
        address indexed _vault,
        address indexed _asset,
        address _sponsor,
        uint256 _amount
    );
    event VaultMarginRedeemed(
        address indexed _vault,
        address indexed _asset,
        address _sponsor,
        uint256 _amount
    );

    /* ----------------------------- admin interface ---------------------------- */
    function createVault(address _user, bytes20 _salt) external authorised {
        bytes memory data = getEncodedVaultInitData(_user);
        bytes32 newsalt = newSalt(_salt, _user);
        address vaultBeaconProxy;
        bytes memory initCode = abi.encodePacked(
            type(BeaconProxy).creationCode,
            abi.encode(vaultBeacon(), data)
        );
        assembly {
            vaultBeaconProxy := create2(
                0,
                add(initCode, 0x20),
                mload(initCode),
                newsalt
            )
        }
        if (vaultBeaconProxy == address(0)) {
            revert FailedDeployVault();
        }
        uint256 numVaults = LibVault.recordVault(_user, vaultBeaconProxy);
        bytes4[] memory sigs = new bytes4[](1);
        sigs[0] = IVault(address(0)).execute.selector;
        LibSecurity.grantPermissions(
            LibAppStorage.ds().auth,
            address(this),
            vaultBeaconProxy,
            sigs
        );
        emit VaultCreated(vaultBeaconProxy, _user, numVaults);
    }

    /* ---------------------- vault configuration interface --------------------- */

    function setVaultBeacon(address _impl) external authorised {
        LibVault.setVaultBeacon(_impl);
    }

    /* ---------------------- user interface --------------------- */
    function withdrawNFT(
        address _vault,
        address _collection,
        uint256 _tokenId
    ) external nonReentrant {
        if (LibVault.getVaultAddress(_msgSender()) != _vault) {
            revert InvalidVaultCall();
        }
        if (LibAppStorage.ds().nftIndex[_collection][_tokenId].isCollateral) {
            revert InvalidWithdrawal();
        }
        delete LibAppStorage.ds().nftIndex[_collection][_tokenId];
        bytes4 selector = IERC721(_collection).transferFrom.selector;
        bytes memory param = abi.encode(_vault, _msgSender(), _tokenId);
        bytes memory data = abi.encodePacked(selector, param);
        bytes memory encodedData = abi.encode(_collection, data);
        IVault(_vault).execute(encodedData);
    }

    function transferReserve(
        address _vault,
        address _currency,
        address _to,
        uint256 _amount
    ) external nonReentrant {
        if (LibVault.getVaultAddress(_msgSender()) != _vault) {
            revert InvalidVaultCall();
        }
        bytes4 selector = IERC20(_currency).transferFrom.selector;
        bytes memory param = abi.encode(_vault, _to, _amount);
        bytes memory data = abi.encodePacked(selector, param);
        bytes memory encodedData = abi.encode(_currency, data);
        IVault(_vault).execute(encodedData);
    }

    /* ---------------------- view functions --------------------- */
    function computeCounterfactualAddress(address _user, bytes20 _salt)
        external
        view
        returns (address)
    {
        bytes memory data = getEncodedVaultInitData(_user);
        bytes memory initCode = abi.encodePacked(
            type(BeaconProxy).creationCode,
            abi.encode(vaultBeacon(), data)
        );
        bytes32 hash = keccak256(
            abi.encodePacked(
                bytes1(0xff),
                address(this),
                newSalt(_salt, _user),
                keccak256(initCode)
            )
        );
        return address(uint160(uint256(hash)));
    }

    function collectionInitialized(address _collection)
        external
        view
        returns (bool)
    {
        return LibAppStorage.ds()._reserveData[_collection].initialized;
    }

    function newSalt(bytes20 _salt, address _owner)
        internal
        pure
        returns (bytes32)
    {
        return
            keccak256(
                abi.encodePacked(keccak256(abi.encodePacked(_owner)), _salt)
            );
    }

    function vaultBeacon() public view returns (address) {
        return LibVault.vaultBeacon();
    }

    function subVaultBeacon() public view returns (address) {
        return LibVault.subVaultBeacon();
    }

    function getVaultAddr(address _user) external view returns (address) {
        return LibVault.getVaultAddress(_user);
    }

    function getEncodedVaultInitData(address _user)
        internal
        view
        returns (bytes memory)
    {
        bytes memory data = abi.encodeWithSelector(
            IVault(address(0)).initialize.selector,
            address(this),
            _user,
            LibAppStorage.ds().paymaster,
            LibAppStorage.ds().WETH9
        );
        return data;
    }
}

/* --------------------------------- errors -------------------------------- */
error InvalidVaultCall();
error FailedDeployVault();
error InvalidWithdrawal();

contracts/voyage/facets/SecurityFacet.so.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";
import {Storage, Authorisation, LibAppStorage} from "../libraries/LibAppStorage.sol";
import {LibSecurity} from "../libraries/LibSecurity.sol";
import {VaultFacet} from "./VaultFacet.sol";

contract SecurityFacet is Storage {
    using LibSecurity for Authorisation;

    event Paused(address account);
    event Unpaused(address account);

    function paused() public view returns (bool) {
        return LibAppStorage.ds()._paused;
    }

    function pause() public authorised {
        LibAppStorage.ds()._paused = true;
        emit Paused(_msgSender());
    }

    function unpause() public authorised {
        LibAppStorage.ds()._paused = false;
        emit Unpaused(_msgSender());
    }

    function grantRole(
        address user,
        uint8 role,
        bool enabled
    ) public authorised {
        LibSecurity.grantRole(LibAppStorage.ds().auth, user, role, enabled);
    }

    function grantRolePermission(
        uint8 role,
        address target,
        bytes4 sig
    ) public authorised {
        LibSecurity.grantRolePermission(
            LibAppStorage.ds().auth,
            role,
            target,
            sig
        );
    }

    function revokeRolePermission(
        uint8 role,
        address target,
        bytes4 sig
    ) public authorised {
        LibSecurity.revokeRolePermission(
            LibAppStorage.ds().auth,
            role,
            target,
            sig
        );
    }

    function grantPermission(
        address src,
        address dst,
        bytes4 sig
    ) public authorised {
        LibSecurity.grantPermission(LibAppStorage.ds().auth, src, dst, sig);
    }

    function revokePermission(
        address src,
        address dst,
        bytes4 sig
    ) public authorised {
        LibSecurity.revokePermission(LibAppStorage.ds().auth, src, dst, sig);
    }

    function isAuthorisedInbound(address src, bytes4 sig)
        public
        returns (bool)
    {
        return
            LibSecurity.isAuthorisedInbound(LibAppStorage.ds().auth, src, sig);
    }

    function isAuthorisedOutbound(address dst, bytes4 sig)
        public
        returns (bool)
    {
        return
            LibSecurity.isAuthorisedOutbound(LibAppStorage.ds().auth, dst, sig);
    }

    function isAuthorised(
        address src,
        address dst,
        bytes4 sig
    ) public returns (bool) {
        return LibSecurity.isAuthorised(LibAppStorage.ds().auth, src, dst, sig);
    }

    function isTrustedForwarder(address _forwarder) public view returns (bool) {
        return LibSecurity.isTrustedForwarder(_forwarder);
    }
}

