// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {BeaconProxy} from "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import {UpgradeableBeacon} from "@openzeppelin/contracts/proxy/beacon/UpgradeableBeacon.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {LibAppStorage, AppStorage, BorrowData, VaultConfig, NFTInfo, DiamondFacet, ReserveConfigurationMap} from "./LibAppStorage.sol";
import {LibReserveConfiguration} from "./LibReserveConfiguration.sol";
import {WadRayMath} from "../../shared/libraries/WadRayMath.sol";
import {PercentageMath} from "../../shared/libraries/PercentageMath.sol";
import {LogarithmMath} from "../../shared/libraries/LogarithmMath.sol";

library LibVault {
    using WadRayMath for uint256;
    using PercentageMath for uint256;
    using LibReserveConfiguration for ReserveConfigurationMap;

    function recordVault(address _owner, address _vault)
        internal
        returns (uint256)
    {
        AppStorage storage s = LibAppStorage.ds();
        require(s.vaultMap[_owner] == address(0), "one vault per owner");
        s.vaults.push(_vault);
        LibAppStorage.ds().vaultMap[_owner] = _vault;
        return (s.vaults.length);
    }

    function setVaultBeacon(address _impl) internal {
        AppStorage storage s = LibAppStorage.ds();
        s.vaultBeacon = new UpgradeableBeacon(_impl);
    }

    /* ----------------------------- view functions ----------------------------- */
    function vaultBeacon() internal view returns (address) {
        AppStorage storage s = LibAppStorage.ds();
        return address(s.vaultBeacon);
    }

    function subVaultBeacon() internal view returns (address) {
        AppStorage storage s = LibAppStorage.ds();
        return address(s.subVaultBeacon);
    }

    function getVaultAddress(address _owner) internal view returns (address) {
        return LibAppStorage.ds().vaultMap[_owner];
    }

    function getVaultDebt(
        address _collection,
        address _currency,
        address _vault
    ) internal view returns (uint256, uint256) {
        AppStorage storage s = LibAppStorage.ds();
        BorrowData storage borrowData = s._borrowData[_collection][_currency][
            _vault
        ];
        return (borrowData.totalPrincipal, borrowData.totalInterest);
    }

    function getDiamondFacets() internal view returns (DiamondFacet memory) {
        AppStorage storage s = LibAppStorage.ds();
        return s.diamondFacet;
    }

    /**
     * @dev Get credit limit for a specific reserve
     * @param _vault vault address
     * @return _collection collection address
     **/
    function getCreditLimit(
        address _vault,
        address _collection,
        address _currency,
        uint256 _fv
    ) internal view returns (uint256) {
        AppStorage storage s = LibAppStorage.ds();
        uint256 rep = s._borrowState[_collection][_currency].repaidTimes[
            _vault
        ];
        uint256 scaledRep = (rep + 1) * 1e18;
        uint256 multiplier = LogarithmMath.log2(scaledRep) + 1;
        return _fv * multiplier;
    }
}

contracts/voyage/libraries/LibSecurity.s.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {LibAppStorage, AppStorage, Authorisation} from "./LibAppStorage.sol";

library LibSecurity {
    function isAuthorised(
        Authorisation storage auth,
        address src,
        address dst,
        bytes4 selector
    ) internal view returns (bool) {
        return
            auth.rbac.canCall(src, dst, selector) ||
            auth.acl.canCall(src, dst, selector);
    }

    function isAuthorisedInbound(
        Authorisation storage auth,
        address src,
        bytes4 selector
    ) internal view returns (bool) {
        // s.security.acl/rbac is an instance ds-roles and implements DSAuthority interface
        return
            auth.rbac.canCall(src, address(this), selector) ||
            auth.acl.canCall(src, address(this), selector);
    }

    function isAuthorisedOutbound(
        Authorisation storage auth,
        address dst,
        bytes4 selector
    ) internal view returns (bool) {
        // s.security.acl/rbac is an instance ds-guard and implements DSAuthority interface
        return
            auth.rbac.canCall(msg.sender, dst, selector) ||
            auth.acl.canCall(msg.sender, dst, selector);
    }

    // role can be a enum, but cast to uint8 before calling grantRole
    function grantRole(
        Authorisation storage auth,
        address user,
        uint8 role,
        bool enabled
    ) internal {
        // grant role
        auth.rbac.setUserRole(user, role, enabled);
    }

    function grantRolePermission(
        Authorisation storage auth,
        uint8 role,
        address target,
        bytes4 sig
    ) internal {
        // give a role a permission
        auth.rbac.setRoleCapability(role, target, sig, true);
    }

    function revokeRolePermission(
        Authorisation storage auth,
        uint8 role,
        address target,
        bytes4 sig
    ) internal {
        // revoke a role permission
        auth.rbac.setRoleCapability(role, target, sig, false);
    }

    function grantPermissions(
        Authorisation storage auth,
        address src,
        address dst,
        bytes4[] memory sig
    ) internal {
        for (uint256 i = 0; i < sig.length; i++) {
            grantPermission(auth, src, dst, sig[i]);
        }
    }

    function grantPermission(
        Authorisation storage auth,
        address src,
        address dst,
        bytes4 sig
    ) internal {
        // allow src to call dst.sig
        auth.acl.permit(src, dst, sig);
    }

    function revokePermission(
        Authorisation storage auth,
        address src,
        address dst,
        bytes4 sig
    ) internal {
        auth.acl.forbid(src, dst, sig);
    }

    function getTrustedForwarder() internal view returns (address) {
        return LibAppStorage.ds().trustedForwarder;
    }

    function setTrustedForwarder(address _forwarder) internal {
        LibAppStorage.ds().trustedForwarder = _forwarder;
    }

    function isTrustedForwarder(address _forwarder)
        internal
        view
        returns (bool)
    {
        return LibAppStorage.ds().trustedForwarder == _forwarder;
    }
}

