// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {VaultFacet} from "../../voyage/facets/VaultFacet.sol";
import {IVault} from "../../vault/Vault.sol";
import {IPaymaster, BasePaymaster, GsnTypes} from "@opengsn/contracts/src/BasePaymaster.sol";
import {IForwarder} from "@opengsn/contracts/src/forwarder/IForwarder.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract VoyagePaymaster is BasePaymaster {
    error SenderNoVault();
    error VaultBalanceInsufficient();

    address public immutable voyage;
    address public immutable weth9;
    address public immutable treasury;

    uint256 public constant REFUND_GAS_OVERHEAD = 35000;
    uint256 public constant PRE_RELAYED_CALL_OVERHEAD = 60000;
    uint256 public constant POST_RELAYED_CALL_OVERHEAD = 80000;
    uint256 public constant ACCEPTANCE_BUDGET =
        PRE_RELAYED_CALL_OVERHEAD + FORWARDER_HUB_OVERHEAD;
    uint256 public constant CALLDATA_LIMIT = type(uint256).max;

    constructor(
        address _voyage,
        address _weth9,
        address _treasury
    ) {
        voyage = _voyage;
        weth9 = _weth9;
        treasury = _treasury;
    }

    function versionPaymaster()
        external
        view
        virtual
        override
        returns (string memory)
    {
        return "1.0.0+voyage.ipaymaster";
    }

    /// @inheritdoc IPaymaster
    function getGasAndDataLimits()
        public
        pure
        override
        returns (IPaymaster.GasAndDataLimits memory limits)
    {
        return
            IPaymaster.GasAndDataLimits(
                ACCEPTANCE_BUDGET,
                PRE_RELAYED_CALL_OVERHEAD,
                POST_RELAYED_CALL_OVERHEAD,
                CALLDATA_LIMIT
            );
    }

    /// @inheritdoc IPaymaster
    function preRelayedCall(
        GsnTypes.RelayRequest calldata relayRequest,
        bytes calldata signature,
        bytes calldata approvalData,
        uint256 maxPossibleGas
    )
        external
        virtual
        override
        returns (bytes memory context, bool revertOnRecipientRevert)
    {
        _verifyForwarder(relayRequest);
        address vault = VaultFacet(voyage).getVaultAddr(
            relayRequest.request.from
        );
        if (vault == address(0)) {
            revert SenderNoVault();
        }
        uint256 ethBalance = vault.balance + IERC20(weth9).balanceOf(vault);
        if (ethBalance < maxPossibleGas * relayRequest.relayData.gasPrice) {
            revert VaultBalanceInsufficient();
        }

        return (abi.encodePacked(relayRequest.request.from), false);
    }

    /// @inheritdoc IPaymaster
    function postRelayedCall(
        bytes calldata context,
        bool success,
        uint256 gasUseWithoutPost,
        GsnTypes.RelayData calldata relayData
    ) external virtual override {
        address vault = abi.decode(context, (address));
        // calldata overhead = 21k + non_zero_bytes * 16 + zero_bytes * 4
        //            ~= 21k + calldata.length * [1/3 * 16 + 2/3 * 4]
        uint256 minimumFees = (gasUseWithoutPost +
            21000 +
            msg.data.length *
            8 +
            REFUND_GAS_OVERHEAD) * relayData.gasPrice;
        uint256 refund = vault.balance >= minimumFees
            ? minimumFees
            : minimumFees + 21000 * relayData.gasPrice; // cover cost of unwrapping WETH
        IVault(vault).refundGas(refund, treasury);
    }
}

contracts/voyage/facets/DataProviderFace.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {WadRayMath} from "../../shared/libraries/WadRayMath.sol";
import {IVToken} from "../interfaces/IVToken.sol";
import {AppStorage, ReserveData, ReserveConfigurationMap, Tranche, VaultConfig, LoanList, RepaymentData, LibAppStorage} from "../libraries/LibAppStorage.sol";
import {LibLiquidity} from "../libraries/LibLiquidity.sol";
import {LibLoan} from "../libraries/LibLoan.sol";
import {LibVault} from "../libraries/LibVault.sol";
import {LibReserveConfiguration} from "../libraries/LibReserveConfiguration.sol";

struct CreditLineData {
    uint256 totalDebt;
    LoanList loanList;
    uint256 gav;
    uint256 ltv;
    uint256 healthFactor;
}

contract DataProviderFacet {
    using WadRayMath for uint256;
    using LibReserveConfiguration for ReserveConfigurationMap;

    struct PoolData {
        address currency;
        uint256 totalLiquidity;
        uint256 juniorLiquidity;
        uint256 seniorLiquidity;
        uint256 juniorLiquidityRate;
        uint256 seniorLiquidityRate;
        uint256 totalDebt;
        uint256 utilizationRate;
        uint256 trancheRatio;
        uint256 decimals;
        string symbol;
        bool isActive;
    }

    struct UserPoolData {
        uint256 juniorTrancheBalance;
        uint256 withdrawableJuniorTrancheBalance;
        uint256 seniorTrancheBalance;
        uint256 withdrawableSeniorTrancheBalance;
        uint256 decimals;
    }

    struct PoolConfiguration {
        uint256 liquidationBonus;
        uint256 loanInterval;
        uint256 loanTenure;
        uint256 incomeRatio;
        bool isInitialized;
        bool isActive;
    }

    function getPoolConfiguration(address _collection)
        external
        view
        returns (PoolConfiguration memory)
    {
        PoolConfiguration memory poolConfiguration;
        ReserveConfigurationMap memory config = LibReserveConfiguration
            .getConfiguration(_collection);
        poolConfiguration.liquidationBonus = config.getLiquidationBonus();
        poolConfiguration.incomeRatio = config.getIncomeRatio();

        return poolConfiguration;
    }

    function getPoolData(address _collection)
        external
        view
        returns (PoolData memory)
    {
        LibLiquidity.DepositAndDebt memory depositAndDebt = LibLiquidity
            .getDepositAndDebt(_collection);
        IERC20Metadata token = IERC20Metadata(depositAndDebt.currency);

        PoolData memory poolData;
        poolData.currency = depositAndDebt.currency;
        poolData.juniorLiquidity = depositAndDebt.juniorDepositAmount;
        poolData.seniorLiquidity = depositAndDebt.seniorDepositAmount;
        poolData.totalLiquidity =
            depositAndDebt.seniorDepositAmount +
            depositAndDebt.juniorDepositAmount;
        poolData.totalDebt = depositAndDebt.totalDebt;
        if (depositAndDebt.seniorDepositAmount == 0) {
            poolData.trancheRatio = 0;
        } else {
            poolData.trancheRatio = depositAndDebt.juniorDepositAmount.rayDiv(
                depositAndDebt.seniorDepositAmount
            );
        }

        poolData.decimals = token.decimals();
        poolData.utilizationRate = LibLiquidity.utilizationRate(_collection);
        poolData.symbol = token.symbol();
        (poolData.isActive, , ) = LibReserveConfiguration
            .getConfiguration(_collection)
            .getFlags();

        return poolData;
    }

    function getDepositTokens(address _collection)
        public
        view
        returns (address senior, address junior)
    {
        ReserveData memory reserve = LibLiquidity.getReserveData(_collection);
        senior = reserve.seniorDepositTokenAddress;
        junior = reserve.juniorDepositTokenAddress;
    }

    function getVault(address _user) external view returns (address) {
        return LibVault.getVaultAddress(_user);
    }

    function getCollections() external view returns (address[] memory) {
        return LibLiquidity.getReserveList();
    }

    function getUserPoolData(address _collection, address _user)
        external
        view
        returns (UserPoolData memory)
    {
        ReserveData memory reserve = LibLiquidity.getReserveData(_collection);
        UserPoolData memory userPoolData;
        IERC20Metadata token = IERC20Metadata(reserve.currency);

        uint256 seniorTrancheWithdrawable = LibLiquidity.balance(
            _collection,
            _user,
            Tranche.SENIOR
        );
        uint256 seniorTrancheUnbonding = LibLiquidity.unbonding(
            _collection,
            _user,
            Tranche.SENIOR
        );
        uint256 seniorTrancheTotalBalance = seniorTrancheWithdrawable +
            seniorTrancheUnbonding;
        uint256 juniorTrancheWithdrawable = LibLiquidity.balance(
            _collection,
            _user,
            Tranche.JUNIOR
        );
        uint256 juniorTrancheUnbonding = LibLiquidity.unbonding(
            _collection,
            _user,
            Tranche.JUNIOR
        );
        uint256 juniorTrancheTotalBalance = juniorTrancheWithdrawable +
            juniorTrancheUnbonding;

        userPoolData.juniorTrancheBalance = juniorTrancheTotalBalance;
        userPoolData
            .withdrawableJuniorTrancheBalance = juniorTrancheWithdrawable;
        userPoolData.seniorTrancheBalance = seniorTrancheTotalBalance;
        userPoolData
            .withdrawableSeniorTrancheBalance = seniorTrancheWithdrawable;
        userPoolData.decimals = token.decimals();
        return userPoolData;
    }

    function getCreditLineData(address _vault, address _collection)
        external
        view
        returns (CreditLineData memory)
    {
        ReserveData memory reserve = LibLiquidity.getReserveData(_collection);
        CreditLineData memory creditLineData;
        uint256 principal;
        uint256 interest;
        LoanList memory loanList;
        (loanList.head, loanList.tail) = LibLoan.getLoanList(
            _collection,
            reserve.currency,
            _vault
        );
        (principal, interest) = LibVault.getVaultDebt(
            _collection,
            reserve.currency,
            _vault
        );
        creditLineData.loanList = loanList;
        creditLineData.totalDebt = principal + interest;
        creditLineData.ltv = creditLineData.totalDebt == 0
            ? 1
            : (creditLineData.gav).rayDiv(creditLineData.totalDebt);
        return creditLineData;
    }

    function getLoanDetail(
        address _vault,
        address _collection,
        uint256 _loanId
    ) external view returns (LibLoan.LoanDetail memory) {
        ReserveData memory reserve = LibLiquidity.getReserveData(_collection);
        return
            LibLoan.getLoanDetail(
                _collection,
                reserve.currency,
                _vault,
                _loanId
            );
    }

    function getRepayment(
        address _valut,
        address _collection,
        uint256 _loanId
    ) external view returns (RepaymentData[] memory) {
        ReserveData memory reserve = LibLiquidity.getReserveData(_collection);
        return
            LibLoan.getRepayment(
                _collection,
                _valut,
                reserve.currency,
                _loanId
            );
    }

    function pendingSeniorWithdrawals(address _user, address _collection)
        public
        view
        returns (uint256)
    {
        ReserveData memory reserve = LibLiquidity.getReserveData(_collection);

        return IVToken(reserve.seniorDepositTokenAddress).unbonding(_user);
    }

    function pendingJuniorWithdrawals(address _user, address _collection)
        public
        view
        returns (uint256)
    {
        ReserveData memory reserve = LibLiquidity.getReserveData(_collection);

        return IVToken(reserve.juniorDepositTokenAddress).unbonding(_user);
    }

    function getProtocolFeeParam() public view returns (address, uint256) {
        AppStorage storage s = LibAppStorage.ds();
        return (s.protocolFee.treasuryAddress, s.protocolFee.cutRatio);
    }
}

contracts/voyage/facets/ConfigurationFac.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {LibAppStorage, Storage, ReserveConfigurationMap} from "../libraries/LibAppStorage.sol";
import {LibReserveConfiguration} from "../libraries/LibReserveConfiguration.sol";
import {LibVault} from "../libraries/LibVault.sol";

contract ConfigurationFacet is Storage, ReentrancyGuard {
    using LibReserveConfiguration for ReserveConfigurationMap;
    /* --------------------------------- events --------------------------------- */
    event LiquidationConfigurationUpdated(
        address indexed _collection,
        uint256 _liquidationBonus
    );
    event IncomeRatioUpdated(address indexed _collection, uint256 _incomeRatio);
    event MarginParametersUpdated(
        address indexed _collection,
        uint256 _min,
        uint256 _max,
        uint256 _marginRequirement
    );
    event LoanParametersUpdated(
        address indexed _collection,
        uint256 _epoch,
        uint256 _term,
        uint256 _gracePeriod
    );

    /* --------------------------------- errors --------------------------------- */
    error IllegalLoanParameters();
    error IllegalMarginParameters();

    /// @dev maximum size of _liquidationBonus is 2^16, ~600%
    /// @param _collection address of the underlying NFT collection
    /// @param _liquidationBonus liquidation bonus, percentage expressed as basis points
    function setLiquidationBonus(address _collection, uint256 _liquidationBonus)
        external
        authorised
    {
        ReserveConfigurationMap memory conf = LibReserveConfiguration
            .getConfiguration(_collection);
        conf.setLiquidationBonus(_liquidationBonus);
        LibReserveConfiguration.saveConfiguration(_collection, conf);
        emit LiquidationConfigurationUpdated(_collection, _liquidationBonus);
    }

    function setIncomeRatio(address _collection, uint256 _ratio)
        external
        authorised
    {
        ReserveConfigurationMap memory conf = LibReserveConfiguration
            .getConfiguration(_collection);
        conf.setIncomeRatio(_ratio);
        LibReserveConfiguration.saveConfiguration(_collection, conf);
        emit IncomeRatioUpdated(_collection, _ratio);
    }

    function setLoanParams(
        address _collection,
        uint256 _epoch,
        uint256 _term,
        uint256 _gracePeriod
    ) external authorised {
        if (_epoch > _term) {
            revert IllegalLoanParameters();
        }
        ReserveConfigurationMap memory conf = LibReserveConfiguration
            .getConfiguration(_collection);
        conf.setLoanInterval(_epoch);
        conf.setLoanTerm(_term);
        conf.setGracePeriod(_gracePeriod);
        LibReserveConfiguration.saveConfiguration(_collection, conf);
        emit LoanParametersUpdated(_collection, _epoch, _term, _gracePeriod);
    }
}

contracts/shared/diamond/VersionedDiamon.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
pragma experimental ABIEncoderV2;

/******************************************************************************\
* Author: Nick Mudge <nick@perfectabstractions.com> (https://twitter.com/mudgen)
* EIP-2535 Diamond Standard: https://eips.ethereum.org/EIPS/eip-2535
* 
* Implementation of a diamond.
/******************************************************************************/

import {LibDiamond} from "./libraries/LibDiamond.sol";
import {DiamondCutFacet} from "./facets/DiamondCutFacet.sol";
import {DiamondLoupeFacet} from "./facets/DiamondLoupeFacet.sol";
import {OwnershipFacet} from "./facets/OwnershipFacet.sol";
import {DiamondVersionFacet} from "../../voyage/facets/DiamondVersionFacet.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract VersionedDiamond is Initializable {
    // more arguments are added to this struct
    // this avoids stack too deep errors
    struct DiamondArgs {
        address owner;
    }

    function _initialize(
        address _owner,
        address _cutFacet,
        address _loupeFacet,
        address _ownershipFacet
    ) internal {
        LibDiamond.setContractOwner(_owner);
        LibDiamond.addDiamondFunctions(_cutFacet, _loupeFacet, _ownershipFacet);
    }

    // Find facet for function that is called and execute the
    // function if a facet is found and return any value.
    fallback() external payable {
        LibDiamond.DiamondStorage storage ds;
        bytes32 position = LibDiamond.DIAMOND_STORAGE_POSITION;
        assembly {
            ds.slot := position
        }
        address facet = ds.selectorToFacetAndPosition[msg.sig].facetAddress;
        require(facet != address(0), "Diamond: Function does not exist");
        assembly {
            calldatacopy(0, 0, calldatasize())
            let result := delegatecall(gas(), facet, 0, calldatasize(), 0, 0)
            returndatacopy(0, 0, returndatasize())
            switch result
            case 0 {
                revert(0, returndatasize())
            }
            default {
                return(0, returndatasize())
            }
        }
    }

    receive() external payable {}
}

contracts/shared/diamond/facets/DiamondL.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
pragma experimental ABIEncoderV2;

/******************************************************************************\
* Author: Nick Mudge <nick@perfectabstractions.com> (https://twitter.com/mudgen)
* EIP-2535 Diamond Standard: https://eips.ethereum.org/EIPS/eip-2535
/******************************************************************************/

import "../libraries/LibDiamond.sol";
import "../interfaces/IDiamondCut.sol";
import "../interfaces/IDiamondLoupe.sol";
import "../interfaces/IERC165.sol";

contract DiamondLoupeFacet is IDiamondLoupe, IERC165 {
    // Diamond Loupe Functions
    ////////////////////////////////////////////////////////////////////
    /// These functions are expected to be called frequently by tools.
    //
    // struct Facet {
    //     address facetAddress;
    //     bytes4[] functionSelectors;
    // }

    /// @notice Gets all facets and their selectors.
    /// @return facets_ Facet
    function facets() external view override returns (Facet[] memory facets_) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        uint256 numFacets = ds.facetAddresses.length;
        facets_ = new Facet[](numFacets);
        for (uint256 i; i < numFacets; i++) {
            address facetAddress_ = ds.facetAddresses[i];
            facets_[i].facetAddress = facetAddress_;
            facets_[i].functionSelectors = ds
                .facetFunctionSelectors[facetAddress_]
                .functionSelectors;
        }
    }

    /// @notice Gets all the function selectors provided by a facet.
    /// @param _facet The facet address.
    /// @return facetFunctionSelectors_
    function facetFunctionSelectors(address _facet)
        external
        view
        override
        returns (bytes4[] memory facetFunctionSelectors_)
    {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        facetFunctionSelectors_ = ds
            .facetFunctionSelectors[_facet]
            .functionSelectors;
    }

    /// @notice Get all the facet addresses used by a diamond.
    /// @return facetAddresses_
    function facetAddresses()
        external
        view
        override
        returns (address[] memory facetAddresses_)
    {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        facetAddresses_ = ds.facetAddresses;
    }

    /// @notice Gets the facet that supports the given selector.
    /// @dev If facet is not found return address(0).
    /// @param _functionSelector The function selector.
    /// @return facetAddress_ The facet address.
    function facetAddress(bytes4 _functionSelector)
        external
        view
        override
        returns (address facetAddress_)
    {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        facetAddress_ = ds
            .selectorToFacetAndPosition[_functionSelector]
            .facetAddress;
    }

    // This implements ERC-165.
    function supportsInterface(bytes4 _interfaceId)
        external
        view
        override
        returns (bool)
    {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        return ds.supportedInterfaces[_interfaceId];
    }
}

