pragma solidity ^0.8.9;

import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {IPaymaster} from "@opengsn/contracts/src/BasePaymaster.sol";
import {LibAppStorage, AppStorage, Storage, ReserveConfigurationMap, ReserveData} from "../libraries/LibAppStorage.sol";
import {LibReserveConfiguration} from "../libraries/LibReserveConfiguration.sol";
import {LibVault} from "../libraries/LibVault.sol";

contract ConfigurationFacet is Storage, ReentrancyGuard {
    using LibReserveConfiguration for ReserveConfigurationMap;
    /* --------------------------------- events --------------------------------- */
    event LiquidationConfigurationUpdated(
        address indexed _collection,
        uint256 _liquidationBonus
    );
    event IncomeRatioUpdated(address indexed _collection, uint256 _incomeRatio);
    event OptimalLiquidityRatioUpdated(
        address indexed _collection,
        uint256 _optimalRatio
    );
    event MaxTwapStaleness(
        address indexed _collection,
        uint256 _maxTwapStaleness
    );
    event LoanParametersUpdated(
        address indexed _collection,
        uint256 _epoch,
        uint256 _term,
        uint256 _gracePeriod
    );
    event GSNConfigurationUpdated(
        address _paymaster,
        address _trustedForwarder
    );
    event MarketplaceAdapterUpdated(
        address indexed _marketplace,
        address _strategy
    );

    /* --------------------------------- errors --------------------------------- */
    error IllegalLoanParameters();
    error InvalidGSNConfiguration();

    /* --------------------------------- setters -------------------------------- */

    /// @dev maximum size of _liquidationBonus is 2^16, ~600%
    /// @param _collection address of the underlying NFT collection
    /// @param _liquidationBonus liquidation bonus, percentage expressed as basis points
    function setLiquidationBonus(address _collection, uint256 _liquidationBonus)
        external
        authorised
    {
        ReserveConfigurationMap memory conf = LibReserveConfiguration
            .getConfiguration(_collection);
        conf.setLiquidationBonus(_liquidationBonus);
        LibReserveConfiguration.saveConfiguration(_collection, conf);
        emit LiquidationConfigurationUpdated(_collection, _liquidationBonus);
    }

    function setIncomeRatio(address _collection, uint256 _ratio)
        external
        authorised
    {
        ReserveConfigurationMap memory conf = LibReserveConfiguration
            .getConfiguration(_collection);
        conf.setIncomeRatio(_ratio);
        LibReserveConfiguration.saveConfiguration(_collection, conf);
        emit IncomeRatioUpdated(_collection, _ratio);
    }

    function setOptimalLiquidityRatio(address _collection, uint256 _ratio)
        external
        authorised
    {
        ReserveConfigurationMap memory conf = LibReserveConfiguration
            .getConfiguration(_collection);
        conf.setOptimalLiquidityRatio(_ratio);
        LibReserveConfiguration.saveConfiguration(_collection, conf);
        emit OptimalLiquidityRatioUpdated(_collection, _ratio);
    }

    function setMaxTwapStaleness(address _collection, uint256 _maxTwapStaleness)
        external
        authorised
    {
        ReserveConfigurationMap memory conf = LibReserveConfiguration
            .getConfiguration(_collection);
        conf.setMaxTwapStaleness(_maxTwapStaleness);
        LibReserveConfiguration.saveConfiguration(_collection, conf);
        emit MaxTwapStaleness(_collection, _maxTwapStaleness);
    }

    function setLoanParams(
        address _collection,
        uint256 _epoch,
        uint256 _term,
        uint256 _gracePeriod
    ) external authorised {
        if (_epoch > _term) {
            revert IllegalLoanParameters();
        }
        ReserveConfigurationMap memory conf = LibReserveConfiguration
            .getConfiguration(_collection);
        conf.setLoanInterval(_epoch);
        conf.setLoanTerm(_term);
        conf.setGracePeriod(_gracePeriod);
        LibReserveConfiguration.saveConfiguration(_collection, conf);
        emit LoanParametersUpdated(_collection, _epoch, _term, _gracePeriod);
    }

    /// @dev Sets the Voyage paymaster
    /// @param _paymaster the address of the paymaster contract.
    /// @param _trustedForwarder the address of the GSN forwarder contract. Deployed by GSN.
    function setGSNConfiguration(address _paymaster, address _trustedForwarder)
        external
        authorised
    {
        if (_paymaster == address(0) || !Address.isContract(_paymaster)) {
            revert InvalidGSNConfiguration();
        }

        if (
            _trustedForwarder == address(0) ||
            _trustedForwarder != IPaymaster(_paymaster).trustedForwarder()
        ) {
            revert InvalidGSNConfiguration();
        }

        AppStorage storage s = LibAppStorage.ds();
        s.paymaster = _paymaster;
        s.trustedForwarder = _trustedForwarder;
        emit GSNConfigurationUpdated(_paymaster, _trustedForwarder);
    }

    function setInterestRateStrategyAddress(
        address _reserve,
        address _interestRateStrategyAddress
    ) external authorised {
        ReserveData storage reserveData = LibAppStorage.ds()._reserveData[
            _reserve
        ];
        if (!reserveData.initialized) {
            revert ReserveNotInitialized();
        }
        reserveData.interestRateStrategyAddress = _interestRateStrategyAddress;
    }

    function getPaymasterAddr() external view returns (address) {
        return LibAppStorage.ds().paymaster;
    }

    function updateMarketPlaceData(address _marketplace, address _strategy)
        external
        authorised
    {
        LibAppStorage
            .ds()
            .marketPlaceData[_marketplace]
            .adapterAddr = _strategy;
        emit MarketplaceAdapterUpdated(_marketplace, _strategy);
    }

    function upgradeJuniorDepositTokenImpl(address _impl) external authorised {
        LibAppStorage.ds().juniorDepositTokenBeacon.upgradeTo(_impl);
    }

    function upgradeSeniorDepositTokenImpl(address _impl) external authorised {
        LibAppStorage.ds().seniorDepositTokenBeacon.upgradeTo(_impl);
    }

    function getIncomeRatio(address _collection) public view returns (uint256) {
        ReserveConfigurationMap memory conf = LibReserveConfiguration
            .getConfiguration(_collection);
        return conf.getIncomeRatio();
    }

    function getMaxTwapStaleness(address _collection)
        public
        view
        returns (uint256)
    {
        ReserveConfigurationMap memory conf = LibReserveConfiguration
            .getConfiguration(_collection);
        return conf.getMaxTwapStaleness();
    }
}

error ReserveNotInitialized();
","keccak256":"0xdad3007fc529af7013c92f447189fa13874e207b9515e69ea30bd4cf21b11479","license":"GPL-3.0"},"contracts/voyage/facets/SecurityFacet.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {Storage, Authorisation, LibAppStorage} from "../libraries/LibAppStorage.sol";
import {LibSecurity} from "../libraries/LibSecurity.sol";
import {ILiquidityFacet} from "../interfaces/ILiquidityFacet.sol";
import {ConfigurationFacet} from "../facets/ConfigurationFacet.sol";

contract SecurityFacet is Storage {
    using LibSecurity for Authorisation;

    event Paused(address account);
    event Unpaused(address account);
    event RoleGranted(address user, uint8 role, bool enabled);
    event RolePermissionGranted(uint8 role, address target, bytes4 sig);
    event PermissionGranted(address src, address dst, bytes4 sig);
    event RoleRevoked(address user, uint8 role);
    event RolePermissionRevoked(uint8 role, address target, bytes4 sig);
    event PermissionRevoked(address src, address dst, bytes4 sig);

    function paused() public view returns (bool) {
        return LibAppStorage.ds()._paused;
    }

    function pause() public authorised {
        LibAppStorage.ds()._paused = true;
        emit Paused(_msgSender());
    }

    function unpause() public authorised {
        LibAppStorage.ds()._paused = false;
        emit Unpaused(_msgSender());
    }

    function grantRole(
        address user,
        uint8 role,
        bool enabled
    ) public authorised {
        LibSecurity.grantRole(LibAppStorage.ds().auth, user, role, enabled);
        emit RoleGranted(user, role, enabled);
    }

    function grantRolePermission(
        uint8 role,
        address target,
        bytes4 sig
    ) public authorised {
        LibSecurity.grantRolePermission(
            LibAppStorage.ds().auth,
            role,
            target,
            sig
        );
        emit RolePermissionGranted(role, target, sig);
    }

    function revokeRolePermission(
        uint8 role,
        address target,
        bytes4 sig
    ) public authorised {
        LibSecurity.revokeRolePermission(
            LibAppStorage.ds().auth,
            role,
            target,
            sig
        );
        emit RolePermissionRevoked(role, target, sig);
    }

    function grantPermission(
        address src,
        address dst,
        bytes4 sig
    ) public authorised {
        LibSecurity.grantPermission(LibAppStorage.ds().auth, src, dst, sig);
        emit PermissionGranted(src, dst, sig);
    }

    function authorizeConfigurator(address _configurator) public authorised {
        if (_configurator == address(0) || !Address.isContract(_configurator)) {
            revert InvalidConfiguratorContract();
        }
        bytes4[] memory selectors = new bytes4[](15);
        selectors[0] = ILiquidityFacet(address(0)).initReserve.selector;
        selectors[1] = ILiquidityFacet(address(0)).activateReserve.selector;
        selectors[2] = ILiquidityFacet(address(0)).deactivateReserve.selector;
        selectors[3] = ILiquidityFacet(address(0)).updateProtocolFee.selector;
        selectors[4] = ILiquidityFacet(address(0)).updateWETH9.selector;

        selectors[5] = ILiquidityFacet(address(0))
            .upgradePriceOracleImpl
            .selector;
        selectors[6] = ConfigurationFacet(address(0))
            .setLiquidationBonus
            .selector;
        selectors[7] = ConfigurationFacet(address(0)).setIncomeRatio.selector;
        selectors[8] = ConfigurationFacet(address(0))
            .setOptimalLiquidityRatio
            .selector;
        selectors[9] = ConfigurationFacet(address(0))
            .setMaxTwapStaleness
            .selector;
        selectors[10] = ConfigurationFacet(address(0)).setLoanParams.selector;
        selectors[11] = ConfigurationFacet(address(0))
            .updateMarketPlaceData
            .selector;

        selectors[12] = ConfigurationFacet(address(0))
            .setGSNConfiguration
            .selector;
        selectors[13] = ConfigurationFacet(address(0))
            .upgradeJuniorDepositTokenImpl
            .selector;
        selectors[14] = ConfigurationFacet(address(0))
            .upgradeSeniorDepositTokenImpl
            .selector;

        LibSecurity.grantPermissions(
            LibAppStorage.ds().auth,
            _configurator,
            address(this),
            selectors
        );
    }

    function revokePermission(
        address src,
        address dst,
        bytes4 sig
    ) public authorised {
        LibSecurity.revokePermission(LibAppStorage.ds().auth, src, dst, sig);
        emit PermissionRevoked(src, dst, sig);
    }

    function isAuthorisedInbound(address src, bytes4 sig)
        public
        returns (bool)
    {
        return
            LibSecurity.isAuthorisedInbound(LibAppStorage.ds().auth, src, sig);
    }

    function isAuthorisedOutbound(address dst, bytes4 sig)
        public
        returns (bool)
    {
        return
            LibSecurity.isAuthorisedOutbound(LibAppStorage.ds().auth, dst, sig);
    }

    function isAuthorised(
        address src,
        address dst,
        bytes4 sig
    ) public returns (bool) {
        return LibSecurity.isAuthorised(LibAppStorage.ds().auth, src, dst, sig);
    }

    function isTrustedForwarder(address _forwarder) public view returns (bool) {
        return LibSecurity.isTrustedForwarder(_forwarder);
    }
}

error InvalidConfiguratorContract();
