pragma solidity ^0.8.9;

import {UpgradeableBeacon} from "@openzeppelin/contracts/proxy/beacon/UpgradeableBeacon.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {DSRoles} from "../auth/DSRoles.sol";
import {DSGuard} from "../auth/DSGuard.sol";
import {LibSecurity} from "./LibSecurity.sol";
import {IVaultFactory} from "../interfaces/IVaultFactory.sol";
import {LibVault} from "./LibVault.sol";
import {IWETH9} from "../../shared/libraries/LibPayments.sol";
import {IDiamondCut} from "../../shared/diamond/interfaces/IDiamondCut.sol";
import {IDiamondLoupe} from "../../shared/diamond/interfaces/IDiamondLoupe.sol";
import {DiamondCutFacet} from "../../shared/diamond/facets/DiamondCutFacet.sol";

enum Tranche {
    JUNIOR,
    SENIOR
}

struct ReserveData {
    //stores the reserve configuration
    ReserveConfigurationMap configuration;
    uint256 currentBorrowRate;
    //the decimals of the reserve asset
    uint256 decimals;
    address interestRateStrategyAddress;
    address juniorDepositTokenAddress;
    address seniorDepositTokenAddress;
    uint40 juniorLastUpdateTimestamp;
    uint40 seniorLastUpdateTimestamp;
    address currency;
    UpgradeableBeacon priceOracle;
    bool initialized;
}

struct ReserveConfigurationMap {
    //bit 0-15: liquidation bonus (uint16)
    //bit 16-23: decimals (uint8)
    //bit 24: reserve is active
    //bit 25: reserve is frozen
    //bit 26: borrowing is enabled
    //bit 27: reserved
    //bit 28-63: min margin (uint36)
    //bit 64-99: max margin (uint36)
    //bit 100-115: margin requirement (uint16)
    //bit 116-131: income ratio (uint16)
    //bit 132-139: instalment interval (uint8)
    //bit 140-155: loan term (uint16)
    //bit 156-163: repayment grace period (uint8)
    //bit 164-255: unused
    uint256 data;
}

struct PMT {
    uint256 principal;
    uint256 interest;
    uint256 fee;
    uint256 pmt;
}

struct RepaymentData {
    uint256 principal;
    uint256 interest;
    // principal + interest
    uint256 total;
    uint40 paidAt;
    bool isLiquidated;
}

struct Loan {
    uint256 principal;
    uint256 interest;
    uint256 protocolFee;
    // the total intended length of the loan in seconds - e.g., 90 days
    uint40 term;
    // the repayment interval - e.g., 30 days
    uint40 epoch;
    // number of instalments, term / epoch
    uint40 nper;
    // cache of reserve data
    uint256 incomeRatio;
    uint256 takeRatio;
    // the amount to be repaid per instalment (principal + interest)
    PMT pmt;
    // the borrow rate of this loan
    uint256 apr;
    uint40 borrowAt;
    // next due data
    uint40 nextPaymentDue;
    // principal paid
    uint256 totalPrincipalPaid;
    // interest paid
    uint256 totalInterestPaid;
    RepaymentData[] repayments;
    // size of repayments
    uint40 paidTimes;
    uint256[] collateral;
}

struct LoanList {
    uint40 head;
    uint40 tail;
}

struct BorrowData {
    uint40 paidLoanNumber;
    // next draw down number
    uint40 nextLoanNumber;
    uint256 totalPrincipal;
    uint256 totalInterest;
    uint256 totalFee;
    uint256 mapSize;
    mapping(uint256 => Loan) loans;
}

struct RepayRecord {
    uint256 repaidTimes;
    uint256 defaultTimes;
}

struct BorrowState {
    uint256 totalDebt;
    uint256 totalInterest;
    uint256 totalSeniorInterest;
    uint256 totalJuniorInterest;
    uint256 avgBorrowRate;
    mapping(address => RepayRecord) repayRecord;
}

struct ProtocolFee {
    address treasuryAddress;
    uint40 takeRate; // express in Ray
}

struct Authorisation {
    DSRoles rbac;
    DSGuard acl;
}

struct NFTInfo {
    bool isCollateral; // this determines whether the NFT can be transferred out of the Vault.
    address collection;
    uint256 tokenId;
    address currency; // record what currency was used to pay
    uint256 price; // price in ETH
}

struct ERC721AssetInfo {
    address marketplace;
    address erc20Addr;
}

struct UpgradeParam {
    mapping(address => mapping(bytes4 => address)) existingSelectorFacetMap;
    mapping(address => bytes4[]) existingSelectors;
    mapping(address => mapping(bytes4 => bool)) newSelectorSet;
    mapping(address => bytes4[]) newSelectors;
    mapping(address => IDiamondCut.FacetCut[]) facetCuts;
    mapping(address => uint256) facetCutSize;
    mapping(uint256 => bytes4[]) selectorsAdded;
    mapping(uint256 => bytes4[]) selectorsReplaced;
    mapping(uint256 => bytes4[]) selectorsRemoved;
}

struct DiamondFacet {
    address diamondCutFacet;
    address diamondLoupeFacet;
    address ownershipFacet;
}

struct MarketPlaceData {
    address adapterAddr;
}

struct AppStorage {
    /* -------------------------------- plumbing -------------------------------- */
    mapping(bytes32 => address) _addresses;
    /* -------------------------------- liquidity ------------------------------- */
    UpgradeableBeacon seniorDepositTokenBeacon;
    UpgradeableBeacon juniorDepositTokenBeacon;
    // mapping of collection address to reserve data
    mapping(address => ReserveData) _reserveData;
    // List of reserves as a map (reserveId => reserve)
    mapping(uint256 => address) _reserveList;
    uint16 _reservesCount;
    IWETH9 WETH9;
    /* ---------------------------------- debt ---------------------------------- */
    // collection => currency => vault => data
    mapping(address => mapping(address => mapping(address => BorrowData))) _borrowData;
    mapping(address => mapping(address => BorrowState)) _borrowState;
    bool _paused;
    /* ---------------------------------- vault --------------------------------- */
    UpgradeableBeacon subVaultBeacon;
    UpgradeableBeacon vaultBeacon;
    DiamondFacet diamondFacet;
    IVaultFactory vaultFactory;
    address[] vaults;
    // mapping of vault owner to vault instance address
    mapping(address => address) vaultMap;
    // marketplace address => marketplace type
    mapping(address => MarketPlaceData) marketPlaceData;
    // collection => tokenId => info
    mapping(address => mapping(uint256 => NFTInfo)) nftIndex;
    /* ---------------------------------- security --------------------------------- */
    Authorisation auth;
    address trustedForwarder; // GSN IERC2771 receiver
    address paymaster; // VoyagePaymaster address
    /* --------------------------------- protocol fee ------------------------------ */
    ProtocolFee protocolFee;
    /* ---------------------------------- helper --------------------------------- */
    // mapping of sender address to helper maps, need to clear after computing
    UpgradeParam upgradeParam;
}

library LibAppStorage {
    function ds() internal pure returns (AppStorage storage ds) {
        bytes32 storagePosition = keccak256("diamond.storage.voyage");
        assembly {
            ds.slot := storagePosition
        }
    }
}

contract Storage {
    modifier whenPaused() {
        require(LibAppStorage.ds()._paused, "Pausable: not paused");
        _;
    }

    modifier whenNotPaused() {
        require(!LibAppStorage.ds()._paused, "Pausable: paused");
        _;
    }

    modifier authorised() {
        require(auth(), "call is not authorised");
        _;
    }

    modifier vaultOwnerOrVoyage(address _vault, address _sender) {
        checkVaultAddr(_vault, _sender);
        _;
    }

    function auth() internal view returns (bool) {
        return
            LibSecurity.isAuthorisedInbound(
                LibAppStorage.ds().auth,
                _msgSender(),
                msg.sig
            );
    }

    function checkVaultAddr(address _vault, address _sender) internal view {
        if (!Address.isContract(_vault)) {
            revert("InvalidVaultAddress");
        }
        if (
            LibVault.getVaultAddress(_sender) != _vault &&
            _sender != address(this)
        ) {
            revert("InvalidVaultCall");
        }
    }

    /**
     * return the sender of this call.
     * if the call came through our trusted forwarder, return the original sender.
     * otherwise, return `msg.sender`.
     * should be used in the contract anywhere instead of msg.sender
     */
    function _msgSender() internal view virtual returns (address ret) {
        if (
            msg.data.length >= 20 && LibSecurity.isTrustedForwarder(msg.sender)
        ) {
            // At this point we know that the sender is a trusted forwarder,
            // so we trust that the last bytes of msg.data are the verified sender address.
            // extract sender address from the end of msg.data
            assembly {
                ret := shr(96, calldataload(sub(calldatasize(), 20)))
            }
        } else {
            ret = msg.sender;
        }
    }

    /**
     * return the msg.data of this call.
     * if the call came through our trusted forwarder, then the real sender was appended as the last 20 bytes
     * of the msg.data - so this method will strip those 20 bytes off.
     * otherwise (if the call was made directly and not through the forwarder), return `msg.data`
     * should be used in the contract instead of msg.data, where this difference matters.
     */
    function _msgData() internal view virtual returns (bytes calldata ret) {
        if (
            msg.data.length >= 20 && LibSecurity.isTrustedForwarder(msg.sender)
        ) {
            return msg.data[0:msg.data.length - 20];
        } else {
            return msg.data;
        }
    }
}
","keccak256":"0x7c5f02af68777f2f2f17a536896d582cf10c23c379654dec833fd0b5cc44f0e1","license":"GPL-3.0"},"contracts/voyage/libraries/LibReserveConfiguration.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {ReserveData, ReserveConfigurationMap, AppStorage, LibAppStorage} from "./LibAppStorage.sol";

/**
 * @title ReserveConfiguration library
 * @author Voyage
 * @notice Implements the bitmap logic to handle the reserve configuration, inspired by Aave
 **/
library LibReserveConfiguration {
    uint256 constant internal LIQUIDATION_BONUS_MASK =       0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF0000; // prettier-ignore
    uint256 constant internal DECIMAL_MASK =                 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF00FFFF; // prettier-ignore
    uint256 constant internal ACTIVE_MASK =                  0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFEFFFFFF; // prettier-ignore
    uint256 constant internal FROZEN_MASK =                  0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFDFFFFFF; // prettier-ignore
    uint256 constant internal BORROWING_ENABLE_MASK =        0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFBFFFFFF; // prettier-ignore
    uint256 constant internal INCOME_RATIO_MASK =            0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF0000FFFFFFFFFFFFFFFFFFFFFFFFFFFFF; // prettier-ignore
    uint256 constant internal LOAN_INTERVAL_MASK =           0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFF00FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF; // prettier-ignore
    uint256 constant internal LOAN_TERM_MASK =               0xFFFFFFFFFFFFFFFFFFFFFFFFF0000FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF; // prettier-ignore
    uint256 constant internal GRACE_PERIOD_MASK =            0xFFFFFFFFFFFFFFFFFFFFFFF00FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF; // prettier-ignore
    uint256 constant internal OPTIMAL_LIQUIDITY_RATIO_MASK = 0xFFFFFFFFFFFFFFFFFFF0000FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF; // prettier-ignore
    uint256 constant internal MAX_TWAP_STALENESS_MASK      = 0xFFFFFFFFF0000000000FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF; // prettier-ignore

    uint256 internal constant DECIMAL_MASK_BIT_POSITION = 16;
    uint256 internal constant ACTIVE_MASK_BIT_POSITION = 24;
    uint256 internal constant FROZEN_MASK_BIT_POSITION = 25;
    uint256 internal constant BORROWING_ENABLE_MASK_POSITION = 26;
    /// @dev bit 27 reserved
    uint256 internal constant INCOME_RATIO_MASK_BIT_POSITION = 116;
    uint256 internal constant LOAN_INTERVAL_MASK_BIT_POSITION = 132;
    uint256 internal constant LOAN_TERM_MASK_BIT_POSITION = 140;
    uint256 internal constant GRACE_PERIOD_MASK_BIT_POSITION = 156;
    uint256 internal constant OPTIMAL_LIQUIDITY_RATIO_MASK_BIT_POSITION = 164;
    uint256 internal constant MAX_TWAP_STALENESS_MASK_BIT_POSITION = 180;

    uint256 internal constant MAX_VALID_LIQUIDATION_BONUS = 65535; // percentage
    uint256 internal constant MAX_VALID_DECIMALS = 255;
    uint256 internal constant MAX_VALID_INCOME_RATIO = 10000; // percentage, max 100%/10000 bps
    uint256 internal constant MAX_VALID_LOAN_INTERVAL = 255; // days
    uint256 internal constant MAX_VALID_LOAN_TERM = 65535; // days
    uint256 internal constant MAX_VALID_GRACE_PERIOD = 255; // days
    uint256 internal constant MAX_VALID_OPTIMAL_RATIO = type(uint32).max; // percentage
    uint256 internal constant MAX_VALID_TWAP_STALENESS = type(uint40).max;

    error InvalidLiquidationBonus();
    error InvalidDecimals();
    error InvalidIncomeRatio();
    error InvalidLoanInterval();
    error InvalidLoanTerm();
    error InvalidGracePeriod();
    error InvalidOptimalRatio();
    error InvalidMaxTwapStaleness();

    event LiquidationConfigurationUpdated(
        address indexed _asset,
        uint256 _liquidationBonus
    );

    /* --------------------------- receiver functions --------------------------- */

    /**
     * Gets the configuration flags of the reserve
     * @param self The reserve configuration
     * @return The state flags representing active, frozen, borrowing enabled
     **/
    function getFlags(ReserveConfigurationMap memory self)
        internal
        pure
        returns (
            bool,
            bool,
            bool
        )
    {
        uint256 dataLocal = self.data;

        return (
            (dataLocal & ~ACTIVE_MASK) != 0,
            (dataLocal & ~FROZEN_MASK) != 0,
            (dataLocal & ~BORROWING_ENABLE_MASK) != 0
        );
    }

    function setLiquidationBonus(
        ReserveConfigurationMap memory self,
        uint256 liquidationBonus
    ) internal pure {
        if (liquidationBonus > MAX_VALID_LIQUIDATION_BONUS) {
            revert InvalidLiquidationBonus();
        }
        self.data = (self.data & LIQUIDATION_BONUS_MASK) | liquidationBonus;
    }

    function getLiquidationBonus(ReserveConfigurationMap memory self)
        internal
        pure
        returns (uint256)
    {
        return self.data & ~LIQUIDATION_BONUS_MASK;
    }

    function setDecimals(ReserveConfigurationMap memory self, uint256 _decimals)
        internal
        pure
    {
        if (_decimals > MAX_VALID_DECIMALS) {
            revert InvalidDecimals();
        }
        self.data =
            (self.data & DECIMAL_MASK) |
            (_decimals << DECIMAL_MASK_BIT_POSITION);
    }

    function getDecimals(ReserveConfigurationMap memory self)
        internal
        pure
        returns (uint256)
    {
        return (self.data & ~DECIMAL_MASK) >> DECIMAL_MASK_BIT_POSITION;
    }

    /**
     * @dev Gets the active state of the reserve
     * @param self The reserve configuration
     * @param active The active state
     **/
    function setActive(ReserveConfigurationMap memory self, bool active)
        internal
        pure
    {
        self.data =
            (self.data & ACTIVE_MASK) |
            (uint256(active ? 1 : 0) << ACTIVE_MASK_BIT_POSITION);
    }

    function getActive(ReserveConfigurationMap storage self)
        internal
        view
        returns (bool)
    {
        return (self.data & ~ACTIVE_MASK) != 0;
    }

    function setIncomeRatio(ReserveConfigurationMap memory self, uint256 ratio)
        internal
        pure
    {
        if (ratio > MAX_VALID_INCOME_RATIO) {
            revert InvalidIncomeRatio();
        }

        self.data =
            (self.data & INCOME_RATIO_MASK) |
            (ratio << INCOME_RATIO_MASK_BIT_POSITION);
    }

    function getIncomeRatio(ReserveConfigurationMap memory self)
        internal
        pure
        returns (uint256)
    {
        return
            (self.data & ~INCOME_RATIO_MASK) >> INCOME_RATIO_MASK_BIT_POSITION;
    }

    function setOptimalLiquidityRatio(
        ReserveConfigurationMap memory self,
        uint256 ratio
    ) internal pure {
        if (ratio > MAX_VALID_OPTIMAL_RATIO) {
            revert InvalidOptimalRatio();
        }

        self.data =
            (self.data & OPTIMAL_LIQUIDITY_RATIO_MASK) |
            (ratio << OPTIMAL_LIQUIDITY_RATIO_MASK_BIT_POSITION);
    }

    function getOptimalLiquidityRatio(ReserveConfigurationMap memory self)
        internal
        pure
        returns (uint256)
    {
        return
            (self.data & ~OPTIMAL_LIQUIDITY_RATIO_MASK) >>
            OPTIMAL_LIQUIDITY_RATIO_MASK_BIT_POSITION;
    }

    function setLoanInterval(
        ReserveConfigurationMap memory self,
        uint256 interval
    ) internal pure {
        if (interval > MAX_VALID_LOAN_INTERVAL) {
            revert InvalidLoanInterval();
        }
        self.data =
            (self.data & LOAN_INTERVAL_MASK) |
            (interval << LOAN_INTERVAL_MASK_BIT_POSITION);
    }

    function setLoanTerm(ReserveConfigurationMap memory self, uint256 term)
        internal
        pure
    {
        if (term > MAX_VALID_LOAN_TERM) {
            revert InvalidLoanTerm();
        }
        self.data =
            (self.data & LOAN_TERM_MASK) |
            (term << LOAN_TERM_MASK_BIT_POSITION);
    }

    function setMaxTwapStaleness(
        ReserveConfigurationMap memory self,
        uint256 maxTwapStaleness
    ) internal pure {
        if (maxTwapStaleness > MAX_VALID_TWAP_STALENESS) {
            revert InvalidMaxTwapStaleness();
        }
        self.data =
            (self.data & MAX_TWAP_STALENESS_MASK) |
            (maxTwapStaleness << MAX_TWAP_STALENESS_MASK_BIT_POSITION);
    }

    function getMaxTwapStaleness(ReserveConfigurationMap memory self)
        internal
        pure
        returns (uint256)
    {
        return
            (self.data & ~MAX_TWAP_STALENESS_MASK) >>
            MAX_TWAP_STALENESS_MASK_BIT_POSITION;
    }

    function setGracePeriod(
        ReserveConfigurationMap memory self,
        uint256 numDays
    ) internal pure {
        if (numDays > MAX_VALID_GRACE_PERIOD) {
            revert InvalidGracePeriod();
        }
        self.data =
            (self.data & GRACE_PERIOD_MASK) |
            (numDays << GRACE_PERIOD_MASK_BIT_POSITION);
    }

    function getBorrowParams(ReserveConfigurationMap memory self)
        internal
        pure
        returns (uint40, uint40)
    {
        uint256 localData = self.data;
        return (
            uint40(
                (localData & ~LOAN_INTERVAL_MASK) >>
                    LOAN_INTERVAL_MASK_BIT_POSITION
            ),
            uint40((localData & ~LOAN_TERM_MASK) >> LOAN_TERM_MASK_BIT_POSITION)
        );
    }

    function getLiquidationParams(ReserveConfigurationMap memory self)
        internal
        pure
        returns (uint256, uint256)
    {
        uint256 localData = self.data;
        return (
            (localData & ~LIQUIDATION_BONUS_MASK),
            (localData & ~GRACE_PERIOD_MASK) >> GRACE_PERIOD_MASK_BIT_POSITION
        );
    }

    function getConfiguration(address _collection)
        internal
        view
        returns (ReserveConfigurationMap memory)
    {
        AppStorage storage s = LibAppStorage.ds();
        return s._reserveData[_collection].configuration;
    }

    function saveConfiguration(
        address _collection,
        ReserveConfigurationMap memory _conf
    ) internal {
        AppStorage storage s = LibAppStorage.ds();
        s._reserveData[_collection].configuration = _conf;
    }
}
","keccak256":"0x041af6290038bfb8dcfe8c6cee24d444ac2db81dc2668dad4f5681261ea05d4d","license":"GPL-3.0"},"contracts/voyage/libraries/LibSecurity.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import {LibAppStorage, AppStorage, Authorisation} from "./LibAppStorage.sol";

library LibSecurity {
    function isAuthorised(
        Authorisation storage auth,
        address src,
        address dst,
        bytes4 selector
    ) internal view returns (bool) {
        return
            auth.rbac.canCall(src, dst, selector) ||
            auth.acl.canCall(src, dst, selector);
    }

    function isAuthorisedInbound(
        Authorisation storage auth,
        address src,
        bytes4 selector
    ) internal view returns (bool) {
        // s.security.acl/rbac is an instance ds-roles and implements DSAuthority interface
        return
            auth.rbac.canCall(src, address(this), selector) ||
            auth.acl.canCall(src, address(this), selector);
    }

    function isAuthorisedOutbound(
        Authorisation storage auth,
        address dst,
        bytes4 selector
    ) internal view returns (bool) {
        // s.security.acl/rbac is an instance ds-guard and implements DSAuthority interface
        return
            auth.rbac.canCall(msg.sender, dst, selector) ||
            auth.acl.canCall(msg.sender, dst, selector);
    }

    // role can be a enum, but cast to uint8 before calling grantRole
    function grantRole(
        Authorisation storage auth,
        address user,
        uint8 role,
        bool enabled
    ) internal {
        // grant role
        auth.rbac.setUserRole(user, role, enabled);
    }

    function grantRolePermission(
        Authorisation storage auth,
        uint8 role,
        address target,
        bytes4 sig
    ) internal {
        // give a role a permission
        auth.rbac.setRoleCapability(role, target, sig, true);
    }

    function revokeRolePermission(
        Authorisation storage auth,
        uint8 role,
        address target,
        bytes4 sig
    ) internal {
        // revoke a role permission
        auth.rbac.setRoleCapability(role, target, sig, false);
    }

    function grantPermissions(
        Authorisation storage auth,
        address src,
        address dst,
        bytes4[] memory sig
    ) internal {
        for (uint256 i = 0; i < sig.length; i++) {
            grantPermission(auth, src, dst, sig[i]);
        }
    }

    function grantPermission(
        Authorisation storage auth,
        address src,
        address dst,
        bytes4 sig
    ) internal {
        // allow src to call dst.sig
        auth.acl.permit(src, dst, sig);
    }

    function revokePermission(
        Authorisation storage auth,
        address src,
        address dst,
        bytes4 sig
    ) internal {
        auth.acl.forbid(src, dst, sig);
    }

    function getTrustedForwarder() internal view returns (address) {
        return LibAppStorage.ds().trustedForwarder;
    }

    function isTrustedForwarder(address _forwarder)
        internal
        view
        returns (bool)
    {
        return LibAppStorage.ds().trustedForwarder == _forwarder;
    }
}
