pragma solidity ^0.8.9;

import '../../libraries/ownership/Ownable.sol';
import './BaseSecurityEscrow.sol';

contract SecurityDepositEscrow is BaseSecurityEscrow {
    address public vault;

    modifier onlyOwner() {
        require(msg.sender == vault, 'Not vault');
        _;
    }

    constructor(address _vault) {
        vault = _vault;
    }

    function getVersion() external view returns (string memory) {
        string memory version = 'SecurityDepositEscrow 0.0.1';
        return version;
    }

    // placeholder function
    function slash(
        address _reserve,
        address payable _to,
        uint256 _amount
    ) public payable nonReentrant onlyOwner {
        transferToUser(_reserve, _to, _amount);
    }

    function deposit(
        address _reserve,
        address _user,
        uint256 _amount
    ) public payable nonReentrant onlyOwner {
        _deposit(_reserve, _user, _amount);
    }

    function withdraw(
        address _reserve,
        address payable _user,
        uint256 _amount
    ) public onlyOwner {
        _withdraw(_reserve, _user, _amount);
    }
}
