pragma solidity ^0.8.9;

import 'openzeppelin-solidity/contracts/utils/math/SafeMath.sol';
import '../math/WadRayMath.sol';
import '../math/MathUtils.sol';
import '../types/DataTypes.sol';
import '../helpers/Errors.sol';
import '../../component/liquidity/DefaultReserveInterestRateStrategy.sol';
import 'hardhat/console.sol';

/**
 * @title DebtLogic library
 * @author Voyage
 * @notice Implements the logic to update the debt state
 **/
library DebtLogic {
    using SafeMath for uint256;
    using WadRayMath for uint256;

    uint256 internal constant RAY = 1e27;
    uint256 internal constant SECOND_PER_MONTH = 30 days;

    using DebtLogic for DataTypes.BorrowData;
    using DebtLogic for DataTypes.BorrowStat;

    function insertDrawDown(
        DataTypes.BorrowData storage debtData,
        DataTypes.BorrowStat storage borrowStat,
        uint256 principal,
        uint256 term,
        uint256 epoch,
        uint256 apr
    ) public {
        uint256 currentDrawDownNumber = debtData.nextDrawDownNumber;
        DataTypes.DrawDown storage dd = debtData.drawDowns[
            currentDrawDownNumber
        ];
        dd.principal = principal;
        dd.term = term;
        dd.epoch = epoch;
        dd.apr = apr;
        dd.nper = term.div(epoch);
        dd.borrowAt = block.timestamp;

        uint256 principalRay = principal.wadToRay();
        uint256 interestRay = principalRay.rayMul(apr);

        DataTypes.PMT memory pmt;
        pmt.principal = principal.div(dd.nper);
        pmt.interest = interestRay.rayToWad().div(dd.nper);
        pmt.pmt = pmt.principal.add(pmt.interest);
        dd.pmt = pmt;

        dd.nextPaymentDue = dd.borrowAt.add(
            dd.nper.sub(dd.paidTimes).mul(dd.epoch.mul(SECOND_PER_MONTH))
        );
        debtData.nextDrawDownNumber++;
        debtData.mapSize++;
        debtData.totalPrincipal = debtData.totalPrincipal.add(principal);
        debtData.totalInterest = debtData.totalInterest.add(
            interestRay.rayToWad()
        );
        borrowStat.increase(principalRay, interestRay, apr);
    }

    function getDrawDownPeriod(DataTypes.BorrowData storage debtData)
        public
        view
        returns (uint256, uint256)
    {
        return (debtData.paidDrawDownNumber, debtData.nextDrawDownNumber);
    }

    function getDrawDownDetail(
        DataTypes.BorrowData storage debtData,
        uint256 _drawDownId
    ) public view returns (DataTypes.DebtDetail memory) {
        DataTypes.DrawDown storage dd = debtData.drawDowns[_drawDownId];
        DataTypes.DebtDetail memory debtDetail;
        debtDetail.principal = dd.principal;
        debtDetail.term = dd.term;
        debtDetail.epoch = dd.epoch;
        debtDetail.nper = dd.nper;
        debtDetail.pmt = dd.pmt;
        debtDetail.apr = dd.apr;
        debtDetail.borrowAt = dd.borrowAt;
        debtDetail.nextPaymentDue = dd.nextPaymentDue;
        debtDetail.totalInterestPaid = dd.totalInterestPaid;
        debtDetail.totalPrincipalPaid = dd.totalPrincipalPaid;
        debtDetail.paidTimes = dd.paidTimes;
        return debtDetail;
    }

    function repay(
        DataTypes.BorrowData storage debtData,
        DataTypes.BorrowStat storage borrowStat,
        uint256 drawDownNumber,
        uint256 principal,
        uint256 interest
    ) public {
        DataTypes.DrawDown storage dd = debtData.drawDowns[drawDownNumber];
        dd.paidTimes += 1;
        if (dd.paidTimes == dd.nper) {
            delete debtData.drawDowns[drawDownNumber];
        } else {
            dd.totalPrincipalPaid = dd.totalPrincipalPaid.add(principal);
            dd.totalInterestPaid = dd.totalInterestPaid.add(interest);
            DataTypes.RepaymentData memory repayment;
            repayment.interest = interest;
            repayment.principal = principal;
            repayment.total = principal.add(interest);
            repayment.paidAt = uint40(block.timestamp);
            dd.repayments.push(repayment);
            dd.nextPaymentDue = dd.borrowAt.add(
                dd.nper.sub(dd.paidTimes).mul(dd.epoch.mul(SECOND_PER_MONTH))
            );
        }

        debtData.totalPrincipal.sub(principal);
        debtData.totalInterest.sub(interest);
        uint256 interestRay = interest.wadToRay();
        borrowStat.decrease(principal.wadToRay(), interestRay, dd.apr);
    }

    function increase(
        DataTypes.BorrowStat storage borrowStat,
        uint256 principalPay,
        uint256 interestRay,
        uint256 rate
    ) internal {
        uint256 totalDebtRay = borrowStat.totalDebt.wadToRay();
        borrowStat.avgBorrowRate = totalDebtRay
            .rayMul(borrowStat.avgBorrowRate)
            .add(principalPay.rayMul(rate))
            .rayDiv(totalDebtRay.add(principalPay));
        borrowStat.totalDebt = borrowStat.totalDebt.add(
            principalPay.rayToWad()
        );
        borrowStat.totalInterest = borrowStat.totalInterest.add(
            interestRay.rayToWad()
        );
    }

    function decrease(
        DataTypes.BorrowStat storage borrowStat,
        uint256 principalPay,
        uint256 interestRay,
        uint256 rate
    ) internal {
        uint256 totalDebtRay = borrowStat.totalDebt.wadToRay();
        borrowStat.avgBorrowRate = totalDebtRay
            .rayMul(borrowStat.avgBorrowRate)
            .sub(principalPay.rayMul(rate))
            .rayDiv(totalDebtRay.sub(principalPay));
        borrowStat.totalDebt = borrowStat.totalDebt.sub(
            principalPay.rayToWad()
        );
        borrowStat.totalInterest = borrowStat.totalInterest.sub(
            interestRay.rayToWad()
        );
    }
}
","keccak256":"0xcdc441b680aec348816196c3892020ca9681e31283079e4cb2463af6e02576a6","license":"GPL-3.0"},"contracts/libraries/logic/ReserveLogic.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.9;

import 'openzeppelin-solidity/contracts/utils/math/SafeMath.sol';
import '../math/WadRayMath.sol';
import '../math/MathUtils.sol';
import '../types/DataTypes.sol';
import '../helpers/Errors.sol';
import '../../component/liquidity/DefaultReserveInterestRateStrategy.sol';
import 'hardhat/console.sol';

/**
 * @title ReserveLogic library
 * @author Voyage
 * @notice Implements the logic to update the reserves state
 **/
library ReserveLogic {
    using SafeMath for uint256;
    using WadRayMath for uint256;

    uint256 internal constant RAY = 1e27;

    using ReserveLogic for DataTypes.ReserveData;

    enum Tranche {
        JUNIOR,
        SENIOR
    }

    /**
     * @dev Emitted when the state of a reserve is updated
     * @param asset The address of the underlying asset of the reserve
     * @param liquidityRate The new liquidity rate
     * @param stableBorrowRate The new stable borrow rate
     * @param liquidityIndex The new liquidity index
     **/
    event ReserveDataUpdated(
        address indexed asset,
        uint256 liquidityRate,
        uint256 stableBorrowRate,
        uint256 liquidityIndex
    );

    function init(
        DataTypes.ReserveData storage reserve,
        address _juniorDepositTokenAddress,
        address _seniorDepositTokenAddress,
        address _interestRateStrategyAddress,
        address _healthStrategyAddress,
        address _loanStrategyAddress,
        uint256 _optimalIncomeRatio
    ) external {
        reserve.juniorLiquidityIndex = WadRayMath.ray();
        reserve.seniorLiquidityIndex = WadRayMath.ray();
        reserve.juniorDepositTokenAddress = _juniorDepositTokenAddress;
        reserve.seniorDepositTokenAddress = _seniorDepositTokenAddress;
        reserve.interestRateStrategyAddress = _interestRateStrategyAddress;
        reserve.healthStrategyAddress = _healthStrategyAddress;
        reserve.optimalIncomeRatio = _optimalIncomeRatio;
        reserve.loanStrategyAddress = _loanStrategyAddress;
    }

    function updateState(
        DataTypes.ReserveData storage reserve,
        Tranche _tranche
    ) public {
        _updateIndexes(reserve, _tranche);
    }

    function getLiquidityRate(
        DataTypes.ReserveData storage reserve,
        Tranche _tranche
    ) public view returns (uint256) {
        return reserve._getLiquidityRate(_tranche);
    }

    struct UpdateInterestRatesLocalVars {
        uint256 availableLiquidity;
        uint256 juniorLiquidity;
        uint256 seniorLiquidity;
        uint256 liquidityRatio;
        uint256 totalDebt;
        // total liquidity rate
        uint256 newLiquidityRate;
        uint256 effectiveJuniorLiquidityRate;
        uint256 effectSeniorLiquidityRate;
        uint256 newBorrowRate;
        uint256 avgBorrowRate;
    }

    // for the purposes of updating interest rates, we only care about senior tranche liquidity.
    function updateInterestRates(
        DataTypes.ReserveData storage _reserve,
        address _reserveAddress,
        address _juniorDepositTokenAddress,
        address _seniorDepositTokenAddress,
        uint256 _juniorLiquidityAdded,
        uint256 _juniorLiquidityTaken,
        uint256 _seniorLiquidityAdded,
        uint256 _seniorLiquidityTaken,
        uint256 _totalDebt,
        uint256 _avgBorrowRate
    ) public {
        UpdateInterestRatesLocalVars memory vars;

        (vars.totalDebt, vars.avgBorrowRate) = (_totalDebt, _avgBorrowRate);

        (
            vars.newLiquidityRate,
            vars.newBorrowRate
        ) = IReserveInterestRateStrategy(_reserve.interestRateStrategyAddress)
            .calculateInterestRates(
                _reserveAddress,
                _seniorDepositTokenAddress,
                _seniorLiquidityAdded,
                _seniorLiquidityTaken,
                vars.totalDebt,
                vars.avgBorrowRate
            );
        require(
            vars.newLiquidityRate <= type(uint128).max,
            Errors.RL_LIQUIDITY_RATE_OVERFLOW
        );

        vars.seniorLiquidity = IERC20(_seniorDepositTokenAddress).totalSupply();
        vars.juniorLiquidity =
            IERC20(_juniorDepositTokenAddress).totalSupply() +
            _juniorLiquidityAdded -
            _juniorLiquidityTaken;

        if (vars.juniorLiquidity == 0) {
            vars.effectiveJuniorLiquidityRate = 0;
            vars.effectSeniorLiquidityRate = vars.newLiquidityRate;
        } else {
            vars.liquidityRatio = vars.seniorLiquidity.rayDiv(
                vars.juniorLiquidity
            );

            vars.effectiveJuniorLiquidityRate = vars
                .newLiquidityRate
                .rayMul(RAY - _reserve.optimalIncomeRatio)
                .rayMul(vars.liquidityRatio);

            vars.effectSeniorLiquidityRate = vars.newLiquidityRate.rayMul(
                _reserve.optimalIncomeRatio
            );
        }

        _reserve.currentOverallLiquidityRate = vars.newLiquidityRate;
        _reserve.currentJuniorLiquidityRate = vars.effectiveJuniorLiquidityRate;
        _reserve.currentSeniorLiquidityRate = vars.effectSeniorLiquidityRate;

        emit ReserveDataUpdated(
            _reserveAddress,
            vars.newLiquidityRate,
            vars.newBorrowRate,
            vars.newLiquidityRate
        );
    }

    function getNormalizedIncome(
        DataTypes.ReserveData storage reserve,
        Tranche _tranche
    ) internal view returns (uint256) {
        uint40 timestamp;
        uint256 liquidityIndex;
        if (_tranche == Tranche.JUNIOR) {
            timestamp = reserve.juniorLastUpdateTimestamp;
            liquidityIndex = reserve.juniorLiquidityIndex;
        } else {
            timestamp = reserve.seniorLastUpdateTimestamp;
            liquidityIndex = reserve.seniorLiquidityIndex;
        }

        //solium-disable-next-line
        if (timestamp == uint40(block.timestamp)) {
            return liquidityIndex;
        }

        uint256 cumulated = MathUtils
            .calculateLinearInterest(
                reserve._getLiquidityRate(_tranche),
                timestamp
            )
            .rayMul(liquidityIndex);
        return cumulated;
    }

    function _getLiquidityRate(
        DataTypes.ReserveData storage reserve,
        Tranche _tranche
    ) internal view returns (uint256) {
        if (_tranche == Tranche.JUNIOR) {
            return reserve.currentJuniorLiquidityRate;
        } else {
            return reserve.currentSeniorLiquidityRate;
        }
    }

    function _updateIndexes(
        DataTypes.ReserveData storage reserve,
        Tranche _tranche
    ) internal {
        if (_tranche == Tranche.JUNIOR) {
            uint256 previousJuniorLiquidityIndex = reserve.juniorLiquidityIndex;
            uint256 lastJuniorUpdatedTimestamp = reserve
                .juniorLastUpdateTimestamp;
            reserve._updateJuniorLiquidityIndex(
                previousJuniorLiquidityIndex,
                uint40(lastJuniorUpdatedTimestamp)
            );
        } else {
            uint256 previousSeniorLiquidityIndex = reserve.seniorLiquidityIndex;
            uint256 lastSeniorUpdatedTimestamp = reserve
                .seniorLastUpdateTimestamp;
            reserve._updateSeniorLiquidityIndex(
                previousSeniorLiquidityIndex,
                uint40(lastSeniorUpdatedTimestamp)
            );
        }
    }

    function _updateJuniorLiquidityIndex(
        DataTypes.ReserveData storage reserve,
        uint256 juniorLiquidityIndex,
        uint40 timestamp
    ) internal returns (uint256) {
        uint256 juniorLiquidityRate = reserve._getLiquidityRate(Tranche.JUNIOR);
        uint256 newJuniorLiquidityIndex = juniorLiquidityIndex;

        // only cumulating if there is any income being produced
        if (juniorLiquidityRate > 0) {
            uint256 cumulatedLiquidityInterest = MathUtils
                .calculateLinearInterest(juniorLiquidityRate, timestamp);
            newJuniorLiquidityIndex = cumulatedLiquidityInterest.rayMul(
                juniorLiquidityIndex
            );
            reserve.juniorLiquidityIndex = newJuniorLiquidityIndex;
        }

        reserve.juniorLastUpdateTimestamp = uint40(block.timestamp);
        return newJuniorLiquidityIndex;
    }

    function _updateSeniorLiquidityIndex(
        DataTypes.ReserveData storage reserve,
        uint256 seniorLiquidityIndex,
        uint40 timestamp
    ) internal returns (uint256) {
        uint256 seniorLiquidityRate = reserve._getLiquidityRate(Tranche.SENIOR);
        uint256 newSeniorLiquidityIndex = seniorLiquidityIndex;

        if (seniorLiquidityRate > 0) {
            uint256 cumulatedLiquidityInterest = MathUtils
                .calculateLinearInterest(seniorLiquidityRate, timestamp);
            newSeniorLiquidityIndex = cumulatedLiquidityInterest.rayMul(
                seniorLiquidityIndex
            );
            reserve.seniorLiquidityIndex = newSeniorLiquidityIndex;
        }
        reserve.seniorLastUpdateTimestamp = uint40(block.timestamp);
        return newSeniorLiquidityIndex;
    }

    function trancheToBytes32(Tranche tranche) public view returns (bytes32) {
        return bytes32(uint256(tranche));
    }
}
